import React, { useState } from 'react'; // Corrected: { useState }
import './Gallery.css';

// Data for the six gallery cards
const cardData = [
  {
    id: 1,
    title: 'Historic Cityscapes',
    description: 'Explore the timeless architecture of the old world.',
    imageUrl: 'https://images.unsplash.com/photo-1502602898657-3e91760c0341?ixlib=rb-4.0.3&w=400&q=80',
  },
  {
    id: 2,
    title: 'Mountain Retreats',
    description: 'Find serenity in the crisp, alpine air.',
    imageUrl: 'https://images.unsplash.com/photo-1464822759023-fed622ff2c3b?ixlib=rb-4.0.3&w=400&q=80',
  },
  {
    id: 3,
    title: 'Oceanic Wonders',
    description: 'Dive into the vibrant life beneath the waves.',
    imageUrl: 'https://images.unsplash.com/photo-1509316975850-ff9c5deb0cd9?ixlib=rb-4.0.3&w=400&q=80',
  },
  {
    id: 4,
    title: 'Forest Adventures',
    description: 'Get lost in the beauty of lush, green canopies.',
    imageUrl: 'https://images.unsplash.com/photo-1448375240586-882707db888b?ixlib=rb-4.0.3&w=400&q=80',
  },
  {
    id: 5,
    title: 'Desert Landscapes',
    description: 'Witness the stark beauty of the arid plains.',
    imageUrl: 'https://images.unsplash.com/photo-1473580044384-7ba9967e16a0?ixlib=rb-4.0.3&w=400&q=80',
  },
  {
    id: 6,
    title: 'Urban Exploration',
    description: 'Capture the vibrant energy of the modern metropolis.',
    imageUrl: 'https://images.unsplash.com/photo-1480714378408-67cf0d136b79?ixlib=rb-4.0.3&w=400&q=80',
  },
];

function Gallery() {
  const [isExpanded, setIsExpanded] = useState(false);

  const handleToggle = () => {
    setIsExpanded(!isExpanded);
  };

  return (
    <div className="gallery-container">
      <div className="gallery-header">
        <h1>Animated Card Stack</h1>
        <p>Click the stack to expand and collapse the gallery.</p>
      </div>

      <ul className={`card-stack ${isExpanded ? 'expanded' : ''}`} onClick={handleToggle}>
        {cardData.map((card, index) => (
          <li key={card.id} className="card" style={{ '--index': index }}>
            <img src={card.imageUrl} alt={card.title} />
            <div className="card-content">
              <h3>{card.title}</h3>
              <p>{card.description}</p>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Gallery; // Corrected: removed the hyphen
