import React, { useState, useRef, useEffect } from 'react';

const ServiceCard = ({ service, onOpenModal, isModalOpen, isActiveCard, onCardLeave }) => {
  const [isAnimated, setIsAnimated] = useState(false); // For hover/click lift effect

  // This hook now simply resets the card's 'animate' state when the modal closes
  useEffect(() => {
    if (!isModalOpen && isActiveCard) {
      setIsAnimated(false);
    }
  }, [isModalOpen, isActiveCard]);

  // Combines hover and click for a unified experience
  const handleMouseEnter = () => setIsAnimated(true);
  const handleMouseLeave = () => {
    setIsAnimated(false);
    // Notify parent if a card is left, to handle edge cases
    if (onCardLeave) onCardLeave();
  };
  const handleCardClick = () => setIsAnimated(true);

  // The "View details" button now directly opens the modal
  const handleViewDetailsClick = (e) => {
    e.stopPropagation(); // Prevent card body click from firing
    onOpenModal(service);
  };

  return (
    <div 
        id="make-3D-space" 
        onClick={handleCardClick}
        onMouseEnter={handleMouseEnter}
        onMouseLeave={handleMouseLeave}
    >
      <div
        id="product-card"
        className={isAnimated ? 'animate' : ''}
      >
        <div id="product-front">
          <div className="shadow"></div>
          <img src={service.image} alt={service.title} />
          <div className="image_overlay"></div>

          <div className="card-title-container">
            <h2>{service.title}</h2>
          </div>

          <div id="view_details" onClick={handleViewDetailsClick}>
            View Details
          </div>

          <div className="stats">
            <div className="stats-container">
              <span className="product_name">{service.summaryTitle}</span>
              <p>{service.summaryDescription}</p>
              <div className="product-options">
                {service.summaryOptions.map((opt, index) => (
                    <div key={index}>
                        <strong>{opt.type}</strong>
                        <span>{opt.details}</span>
                    </div>
                ))}
              </div>
            </div>
          </div>
        </div>
        {/* The entire product-back div has been removed */}
      </div>
    </div>
  );
};

export default ServiceCard;
