import React, { useEffect, useState, useRef, useCallback } from 'react';
import './Home.css'; // Import the corresponding CSS file

// --- NEW: ImageCarousel Component with Modal Functionality ---
const ImageCarousel = ({ images }) => {
  // State to manage if the modal is open or closed
  const [isModalOpen, setIsModalOpen] = useState(false);
  // State to track the index of the currently selected image
  const [selectedImageIndex, setSelectedImageIndex] = useState(null);
  // State to pause the carousel animation on hover
  const [isPaused, setIsPaused] = useState(false);
  // Ref to store the scroll position to return to it later
  const scrollPositionRef = useRef(0);

  /**
   * Opens the modal and displays the clicked image.
   * @param {number} index - The index of the image that was clicked.
   */
  const handleImageClick = (index) => {
    setSelectedImageIndex(index);
    setIsModalOpen(true);
    // Store the current scroll position
    scrollPositionRef.current = window.scrollY;
  };

  /**
   * Closes the modal.
   * The useCallback hook is used for optimization, preventing the function from being recreated on every render.
   */
  const handleCloseModal = useCallback(() => {
    setIsModalOpen(false);
    setSelectedImageIndex(null);
  }, []);

  /**
   * Navigates to the next image in the gallery.
   * e.stopPropagation() prevents the click from closing the modal.
   */
  const handleNextImage = useCallback((e) => {
    e.stopPropagation();
    setSelectedImageIndex(prevIndex => (prevIndex + 1) % images.length);
  }, [images.length]);

  /**
   * Navigates to the previous image in the gallery.
   * e.stopPropagation() prevents the click from closing the modal.
   */
  const handlePrevImage = useCallback((e) => {
    e.stopPropagation();
    setSelectedImageIndex(prevIndex => (prevIndex - 1 + images.length) % images.length);
  }, [images.length]);

  // This effect hook handles the logic when the modal's state changes.
  useEffect(() => {
    const appRoot = document.getElementById('root');

    if (isModalOpen) {
      // Add a class to blur the background content
      appRoot.classList.add('body-blur');
      // A trick to "freeze" the background while allowing the modal to be scrollable if needed.
      document.body.style.top = `-${scrollPositionRef.current}px`;
      document.body.style.position = 'fixed';
      document.body.style.width = '100%';
    } else {
      // Clean up styles when the modal is closed
      appRoot.classList.remove('body-blur');
      document.body.style.position = '';
      document.body.style.top = '';
      document.body.style.width = '';
      // Restore the original scroll position
      window.scrollTo(0, scrollPositionRef.current);
    }

    // Add keyboard navigation for the modal
    const handleKeyDown = (e) => {
      if (isModalOpen) {
        if (e.key === 'Escape') handleCloseModal();
        if (e.key === 'ArrowRight') handleNextImage(e);
        if (e.key === 'ArrowLeft') handlePrevImage(e);
      }
    };

    window.addEventListener('keydown', handleKeyDown);

    // Cleanup function to remove the event listener
    return () => {
      window.removeEventListener('keydown', handleKeyDown);
      // Ensure styles are removed if the component unmounts
      appRoot.classList.remove('body-blur');
      document.body.style.position = '';
      document.body.style.top = '';
      document.body.style.width = '';
    };
  }, [isModalOpen, handleCloseModal, handleNextImage, handlePrevImage]);


  // We duplicate the images to create a seamless infinite scroll effect
  const imageList = [...images, ...images];

  return (
    <div
      className="carousel-container"
      onMouseEnter={() => setIsPaused(true)}
      onMouseLeave={() => setIsPaused(false)}
    >
      <div className={`carousel-track top-track ${isPaused ? 'paused' : ''}`}>
        {imageList.map((img, index) => (
          // When a card is clicked, we call handleImageClick with its unique index
          <div className="carousel-card" key={`top-${index}`} onClick={() => handleImageClick(index % images.length)}>
            <img src={img} alt={`Gallery of successful team events ${index + 1}`} />
          </div>
        ))}
      </div>
      <div className={`carousel-track bottom-track ${isPaused ? 'paused' : ''}`}>
        {imageList.map((img, index) => (
          <div className="carousel-card" key={`bottom-${index}`} onClick={() => handleImageClick(index % images.length)}>
            <img src={img} alt={`Gallery of corporate culture workshops ${index + 1}`} />
          </div>
        ))}
      </div>

      {/* --- MODAL MARKUP --- */}
      {/* This block of JSX will only render if isModalOpen is true */}
      {isModalOpen && selectedImageIndex !== null && (
        <div className="carousel-modal" onClick={handleCloseModal}>
          {/* Close Button */}
          <button className="modal-close-button" onClick={handleCloseModal} aria-label="Close image view">
            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="2" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" d="M6 18L18 6M6 6l12 12" />
            </svg>
          </button>

          {/* Previous Button */}
          <button className="modal-nav-button modal-prev" onClick={handlePrevImage} aria-label="Previous image">
             <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="2" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" d="M15 19l-7-7 7-7" />
             </svg>
          </button>

          {/* Image Container */}
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <img src={images[selectedImageIndex]} alt="Full screen view of event" />
          </div>

          {/* Next Button */}
          <button className="modal-nav-button modal-next" onClick={handleNextImage} aria-label="Next image">
             <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="2" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" d="M9 5l7 7-7 7" />
             </svg>
          </button>
        </div>
      )}
    </div>
  );
};


// --- Main Home Component (remains largely the same) ---
const Home = () => {
    const [scrollY, setScrollY] = useState(0);
    const scrollerRef = useRef(null);
    const testimonialsContainerRef = useRef(null);

    const galleryImages = [
      "https://images.unsplash.com/photo-1511795409834-ef04bbd61622?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
      "https://images.unsplash.com/photo-1530103862676-de8c9debad1d?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
      "https://images.unsplash.com/photo-1519167758481-83f29b1fe26d?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
      "https://images.unsplash.com/photo-1464366400600-7168b8af9bc3?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
      "https://images.unsplash.com/photo-1558618666-fcd25c85cd64?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
      "https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
      "https://images.unsplash.com/photo-1492684223066-81342ee5ff30?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
      "https://images.unsplash.com/photo-1523580494863-6f3031224c94?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80"
    ];

    const heroImage = "https://images.unsplash.com/photo-1511795409834-ef04bbd61622?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1920&q=80";

    const servicesImage = "https://images.unsplash.com/photo-1530103862676-de8c9debad1d?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1920&q=80";

    const testimonials = [
      {
        quote: "Working with this team was an absolute dream. They took our vision and brought it to life with such elegance and precision.",
        author: "Sarah & Tom",
        event: "Wedding Celebration",
        image: "https://images.unsplash.com/photo-1500048993953-d23a436266cf?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=800&q=80"
      },
      {
        quote: "The professionalism and creativity are unmatched. The feedback from attendees was overwhelmingly positive. Flawlessly executed.",
        author: "Johnathan Lee",
        event: "CEO, Innovate Corp.",
        image: "https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=800&q=80"
      },
      {
        quote: "From the initial consultation to the day of the event, the communication was seamless. They are true experts in their field.",
        author: "Maria Garcia",
        event: "50th Anniversary Party",
        image: "https://images.unsplash.com/photo-1438761681033-6461ffad8d80?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=800&q=80"
      },
      {
          quote: "I was truly impressed by their attention to detail. Every element was thoughtfully considered and beautifully implemented.",
          author: "Aisha Khan",
          event: "Product Launch Event",
          image: "https://images.unsplash.com/photo-1508214751196-bcfd4ca60f91?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=800&q=80"
      },
      {
          quote: "They transformed our charity fundraiser into an unforgettable evening, helping us exceed our fundraising goals.",
          author: "David Chen",
          event: "Director, Unity Foundation",
          image: "https://images.unsplash.com/photo-1506794778202-cad84cf45f1d?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=800&q=80"
      },
      {
          quote: "An incredible team that is both organized and inspired. They took all the stress out of planning and allowed us to simply enjoy our special day.",
          author: "Emily & Mark",
          event: "Private Estate Wedding",
          image: "https://images.unsplash.com/photo-1544005313-94ddf0286df2?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=800&q=80"
      }
    ];

    const extendedTestimonials = [...testimonials, ...testimonials];

