    'https://logodix.com/logo/1722880.png',
    'https://logodix.com/logo/1722880.png',
    'https://logodix.com/logo/1722880.png',
    'https://logodix.com/logo/1722880.png',
  ];

  // Effect for parallax and section animations
  useEffect(() => {
    const handleScroll = () => setScrollY(window.scrollY);
