        .client-logo-ribbon {
          background-color: #ffffff;
          padding: 3rem 0;
          border-top: 1px solid #edeae6;
          overflow: hidden;
        }

        .logo-track-wrapper {
          overflow: hidden;
          -webkit-mask-image: linear-gradient(to right, transparent 0%, black 10%, black 90%, transparent 100%);
          mask-image: linear-gradient(to right, transparent 0%, black 10%, black 90%, transparent 100%);
        }

        .logo-track {
            display: flex;
            width: fit-content;
            animation: scroll-left 40s linear infinite;
        }

        .client-logo {
            height: 40px;
            margin: 0 3rem;
            opacity: 0.6;
            transition: opacity 0.3s ease;
            filter: grayscale(100%);
        }

        .client-logo:hover {
            opacity: 1;
            filter: grayscale(0%);
        }

        /* --- SECTION 4: GALLERY CAROUSEL --- */
        .gallery-carousel-section {
          background-color: #ffffff;
          padding: 6rem 0;
          text-align: center;
          overflow: visible;
        }

        .gallery-carousel-header {
          max-width: 800px;
          margin: 0 auto 3rem auto;
          padding: 0 2rem;
        }

        .gallery-carousel-header h2 {
          font-family: 'Inter', sans-serif;
          font-weight: 400;
          font-size: 1.3rem;
          letter-spacing: 0.3em;
          color: #8b7355;
          text-transform: uppercase;
          margin-bottom: 1.5rem;
        }

        .gallery-carousel-header p {
          font-family: 'EB Garamond', serif;
          font-size: 1.3rem;
          font-weight: 400;
          line-height: 1.6;
          color: #6b5b4d;
          margin-bottom: 0;
        }

        .gallery-carousel-cta {
          margin-top: 3rem;
        }

        .discover-button-dark {
          display: inline-block;
          padding: 14px 40px;
          border: 1.5px solid #8b7355;
          color: #8b7355;
          background-color: transparent;
          font-family: 'Inter', sans-serif;
          font-size: 0.75rem;
          font-weight: 500;
          letter-spacing: 0.15em;
          text-decoration: none;
          text-transform: uppercase;
          transition: all 0.3s ease;
        }

        .discover-button-dark:hover {
          background-color: #8b7355;
          color: #ffffff;
          transform: translateY(-1px);
        }

        /* --- IMAGE CAROUSEL STYLES --- */
        @keyframes scroll-left {
          0% { transform: translateX(0); }
          100% { transform: translateX(-50%); }
        }

        .carousel-container {
          width: 100%;
          overflow: hidden;
          padding: 2rem 0;
          position: relative;
          -webkit-mask-image: linear-gradient(to right, transparent 0%, black 5%, black 95%, transparent 100%);
          mask-image: linear-gradient(to right, transparent 0%, black 5%, black 95%, transparent 100%);
        }

        .carousel-track {
          display: flex;
          width: fit-content;
          animation: scroll-left 50s linear infinite;
        }

        .carousel-track.bottom-track {
          animation-direction: reverse;
          margin-top: 1rem;
        }

        .carousel-track.paused {
          animation-play-state: paused;
        }

        .carousel-card {
          flex-shrink: 0;
          width: 350px;
          height: 250px;
          margin: 0 0.5rem;
          border-radius: 12px;
          overflow: hidden;
          box-shadow: 0 8px 16px rgba(0, 0, 0, 0.1);
          transition: transform 0.4s cubic-bezier(0.165, 0.84, 0.44, 1), box-shadow 0.4s cubic-bezier(0.165, 0.84, 0.44, 1);
          cursor: pointer;
          border: 1px solid rgba(0, 0, 0, 0.05);
        }

        .carousel-card:hover {
          transform: scale(1.05) translateY(-5px);
          box-shadow: 0 12px 24px rgba(0, 0, 0, 0.15);
        }

        .carousel-card img {
          width: 100%;
          height: 100%;
          object-fit: cover;
        }

        /* --- STYLES FOR MODAL AND BLUR EFFECT --- */
        #root.body-blur > *:not(.carousel-modal) {
          filter: blur(10px) brightness(0.7);
          transition: filter 0.4s ease-out;
          pointer-events: none; 
        }

        #root.body-blur .carousel-modal {
            filter: none;
            pointer-events: auto;
        }

        .carousel-modal {
          position: fixed;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          display: flex;
          align-items: center;
          justify-content: center;
          z-index: 2000;
          background-color: rgba(0, 0, 0, 0.8);
          padding: 2rem;
          box-sizing: border-box;
        }

        .modal-content {
          position: relative;
          max-width: 90vw;
          max-height: 90vh;
          display: flex;
          align-items: center;
          justify-content: center;
        }

        .modal-content img {
          max-width: 100%;
          max-height: 90vh;
          object-fit: contain;
          border-radius: 8px;
          box-shadow: 0 4px 20px rgba(0, 0, 0, 0.3);
        }

        .modal-close-button {
          position: absolute;
          top: 2rem;
          right: 2rem;
          background: rgba(0, 0, 0, 0.5);
          border: none;
          border-radius: 50%;
          width: 48px;
          height: 48px;
          cursor: pointer;
          display: flex;
          align-items: center;
          justify-content: center;
          color: white;
          transition: all 0.3s ease;
          z-index: 2001;
        }

        .modal-close-button:hover {
          background: rgba(0, 0, 0, 0.7);
          transform: rotate(90deg);
        }

        .modal-nav-button {
          position: absolute;
          top: 50%;
          transform: translateY(-50%);
          background: rgba(0, 0, 0, 0.5);
          border: none;
          border-radius: 50%;
          width: 48px;
          height: 48px;
          cursor: pointer;
          display: flex;
          align-items: center;
          justify-content: center;
          color: white;
          transition: all 0.3s ease;
          z-index: 2001;
        }

        .modal-nav-button:hover {
          background: rgba(0, 0, 0, 0.7);
          transform: translateY(-50%) scale(1.1);
        }

        .modal-nav-button.modal-prev {
          left: 2rem;
        }

        .modal-nav-button.modal-next {
          right: 2rem;
        }

        .modal-nav-button svg {
          width: 24px;
          height: 24px;
        }

        /* --- RESPONSIVE STYLES --- */
        @media (max-width: 900px) {
          .services-section { flex-direction: column-reverse; }
          .services-text-column, .services-image-column { width: 100%; min-height: 400px; }
          .services-text-column { padding: 3rem 2rem; }
          .hero-text-content { margin-bottom: 3rem; }
          .hero-scroll-indicator { bottom: 2rem; }
          .testimonial-card { flex: 0 0 320px; }
          .testimonials-arrow-right { right: 1rem; }
          .modal-nav-button.modal-prev { left: 1rem; }
          .modal-nav-button.modal-next { right: 1rem; }
        }

        @media (max-width: 600px) {
          .services-content h2 { font-size: 1rem; letter-spacing: 0.3em; }
          .services-content p { font-size: 1rem; }
          .discover-button, .discover-button-dark { padding: 12px 30px; font-size: 0.7rem; }
          .carousel-card { width: 300px; height: 215px; }
          .modal-close-button { top: 1rem; right: 1rem; width: 40px; height: 40px; }
          .modal-close-button svg { width: 24px; height: 24px; }
          .testimonials-section { padding: 6rem 0; }
          .testimonials-grid { padding-left: 1.5rem; padding-right: 1.5rem; }
          .testimonial-card { flex: 0 0 280px; padding: 2rem; margin-right: 1.5rem; }
          .testimonials-arrow-right { display: none; }
          .modal-nav-button { width: 40px; height: 40px; }
          .modal-nav-button svg { width: 24px; height: 24px; }
          .modal-nav-button.modal-prev { left: 0.5rem; }
          .modal-nav-button.modal-next { right: 0.5rem; }
          .modal-content img { max-width: calc(100vw - 100px); }
        }
      `
