// src/components/ServiceCard.js
import React, { useState, useRef } from 'react';

const ServiceCard = ({ service, onOpenModal }) => {
  const [cardClasses, setCardClasses] = useState('');
  const cardRef = useRef(null);

  const handleViewDetails = () => {
    // This is the flip animation
    let newClasses = 'flip-10';
    setCardClasses(newClasses);
    setTimeout(() => {
      newClasses += ' flip90';
      setCardClasses(newClasses);
      if (cardRef.current) cardRef.current.querySelector('#product-front').style.display = 'none';
    }, 150);
    setTimeout(() => {
      newClasses += ' flip190';
      setCardClasses(newClasses);
      if (cardRef.current) cardRef.current.querySelector('#product-back').style.display = 'block';
    }, 300);
    setTimeout(() => {
      newClasses += ' flip180';
      setCardClasses(newClasses);
    }, 450);
    setTimeout(() => {
      if (cardRef.current) {
        cardRef.current.querySelector('#cx').classList.add('s1', 's2', 's3');
        cardRef.current.querySelector('#cy').classList.add('s1', 's2', 's3');
      }
    }, 600);
  };

  const handleFlipBack = () => {
    // This flips it back to the front
    // (You can implement the reverse animation if needed)
  };

  return (
    <div id="make-3D-space">
      <div
        id="product-card"
        ref={cardRef}
        className={cardClasses}
        onMouseEnter={() => setCardClasses('animate')}
        onMouseLeave={() => setCardClasses('')}
      >
        <div id="product-front">
          <div className="shadow"></div>
          <img src={service.image} alt={service.title} />
          <div className="image_overlay"></div>
          <div id="view_details" onClick={handleViewDetails}>View details</div>
          <div className="stats">
            <div className="stats-container">
              <span className="product_name">{service.summaryTitle}</span>
              <p>{service.summaryDescription}</p>
              <div className="product-options">
                {service.summaryOptions.map((opt, index) => (
                    <div key={index}>
                        <strong>{opt.type}</strong>
                        <span>{opt.details}</span>
                    </div>
                ))}
              </div>
            </div>
          </div>
        </div>
        <div id="product-back" style={{ display: 'none' }}>
          <div className="shadow"></div>
          <div id="flip-back" onClick={handleFlipBack}>
            <div id="cy"></div>
            <div id="cx"></div>
          </div>
          <div className="back-content">
              <h2>{service.title}</h2>
              <p>Ready to learn more about how we can make your event unforgettable?</p>
              <button className="view-full-details-btn" onClick={() => onOpenModal(service)}>
                View Full Details
              </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ServiceCard;
