import React, { useState, useRef, useCallback, useEffect } from 'react';
import './TestimonialCarousel.css';

const TestimonialCarousel = ({ testimonials }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isPaused, setIsPaused] = useState(false);
  const carouselRef = useRef(null);
  const itemRef = useRef(null);
  const autoScrollRef = useRef(null);
  const visibleItems = window.innerWidth < 768 ? 1 : 3;

  const nextTestimonial = useCallback(() => {
    setCurrentIndex(prev => (prev + 1) % (testimonials.length - (window.innerWidth < 768 ? 0 : 2)));
    resetAutoScroll();
  }, [testimonials.length]);

  const prevTestimonial = useCallback(() => {
    setCurrentIndex(prev => (prev - 1 + (testimonials.length - (window.innerWidth < 768 ? 0 : 2))) % 
      (testimonials.length - (window.innerWidth < 768 ? 0 : 2)));
    resetAutoScroll();
  }, [testimonials.length]);

  const resetAutoScroll = useCallback(() => {
    clearInterval(autoScrollRef.current);
    autoScrollRef.current = setInterval(() => {
      if (!isPaused) nextTestimonial();
    }, 5000);
  }, [isPaused, nextTestimonial]);

  useEffect(() => {
    const handleResize = () => {
      setCurrentIndex(0);
    };

    window.addEventListener('resize', handleResize);
    resetAutoScroll();

    return () => {
      clearInterval(autoScrollRef.current);
      window.removeEventListener('resize', handleResize);
    };
  }, [resetAutoScroll]);

  const getTransform = () => {
    if (!itemRef.current) return 'translateX(0)';
    const itemWidth = itemRef.current.offsetWidth + 32;
    return `translateX(-${currentIndex * itemWidth}px)`;
  };

  return (
    <div 
      className="testimonial-carousel-container"
      onMouseEnter={() => {
        setIsPaused(true);
        clearInterval(autoScrollRef.current);
      }}
      onMouseLeave={() => {
        setIsPaused(false);
        resetAutoScroll();
      }}
    >
      <button 
        className="carousel-nav-button prev" 
        onClick={prevTestimonial}
        aria-label="Previous testimonial"
      >
        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
        </svg>
      </button>

      <div className="testimonial-carousel-wrapper">
        <div 
          className="testimonial-carousel" 
          style={{ transform: getTransform() }}
        >
          {testimonials.map((testimonial, index) => (
            <div 
              key={index} 
              ref={index === 0 ? itemRef : null}
              className="testimonial-card"
            >
              <div className="testimonial-image-container">
                <img src={testimonial.image} alt={testimonial.author} className="testimonial-image" />
              </div>
              <div className="testimonial-content">
                <p className="testimonial-quote">"{testimonial.quote}"</p>
                <div className="testimonial-meta">
                  <span className="testimonial-author">{testimonial.author}</span>
                  <span className="testimonial-event">{testimonial.event}</span>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      <button 
        className="carousel-nav-button next" 
        onClick={nextTestimonial}
        aria-label="Next testimonial"
      >
        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
        </svg>
      </button>
    </div>
  );
};

export default TestimonialCarousel;
