import React, { useState, useRef, useEffect } from 'react';

const ServiceCard = ({ service, onOpenModal, isModalOpen, isActiveCard }) => {
  const [isFlipped, setIsFlipped] = useState(false);
  const [isAnimated, setIsAnimated] = useState(false); // For hover/click lift effect
  const cardRef = useRef(null);

  // This hook resets the card to its original state when the modal is closed.
  useEffect(() => {
    if (!isModalOpen && isFlipped) {
      // Use a "leaving" class for a smooth transition back
      cardRef.current.classList.add('leaving');
      setTimeout(() => {
        setIsFlipped(false);
        setIsAnimated(false);
        if (cardRef.current) {
          cardRef.current.classList.remove('leaving');
        }
      }, 600); // Duration should match the CSS transition
    }
  }, [isModalOpen, isFlipped]);

  // Handler for the main card body click (for touch devices)
  const handleCardClick = () => {
    if (!isFlipped) {
      setIsAnimated(!isAnimated);
    }
  };

  // Flips the card when "View details" on the front is clicked
  const handleFlip = (e) => {
    e.stopPropagation(); // Prevent card body click from firing
    if (!isFlipped) {
      setIsFlipped(true);
      setIsAnimated(true); // Keep the card lifted while it's flipped
    }
  };

  return (
    <div 
        id="make-3D-space" 
        onClick={handleCardClick}
        onMouseEnter={() => !isFlipped && setIsAnimated(true)}
        onMouseLeave={() => !isFlipped && setIsAnimated(false)}
    >
      <div
        id="product-card"
        ref={cardRef}
        // Apply classes based on state for CSS to handle animations
        className={`${isAnimated ? 'animate' : ''} ${isFlipped ? 'flipped' : ''}`}
      >
        <div id="product-front">
          <div className="shadow"></div>
          <img src={service.image} alt={service.title} />
          <div className="image_overlay"></div>

          {/* Always-visible title */}
          <div className="card-title-container">
            <h2>{service.title}</h2>
          </div>

          <div id="view_details" onClick={handleFlip}>View details</div>

          <div className="stats">
            <div className="stats-container">
              <span className="product_name">{service.summaryTitle}</span>
              <p>{service.summaryDescription}</p>
              <div className="product-options">
                {service.summaryOptions.map((opt, index) => (
                    <div key={index}>
                        <strong>{opt.type}</strong>
                        <span>{opt.details}</span>
                    </div>
                ))}
              </div>
            </div>
          </div>
        </div>

        <div id="product-back">
          <div className="shadow"></div>
          <div id="flip-back" onClick={(e) => {e.stopPropagation(); setIsFlipped(false)}}>
            <div id="cy"></div>
            <div id="cx"></div>
          </div>
          <div className="back-content">
              <h2>{service.title}</h2>
              <p>Ready to learn more about how we can make your event unforgettable?</p>
              <button className="view-full-details-btn" onClick={(e) => { e.stopPropagation(); onOpenModal(service); }}>
                View Full Details
              </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ServiceCard;
