import React, { useState, useEffect } from 'react';
import ServiceCard from '../components/ServiceCard';
import ServiceDetailModal from '../components/ServiceDetailModal';
import { services } from '../data/servicesData';
import '../styles/Services.css';

const Services = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedService, setSelectedService] = useState(null);

  const handleOpenModal = (service) => {
    setSelectedService(service);
    setIsModalOpen(true);
  };

  // This function now fully resets the state.
  const handleCloseModal = () => {
    setIsModalOpen(false);
    // By setting the service to null AFTER the animation, we ensure a smooth exit.
  };

  useEffect(() => {
    if (isModalOpen) {
      document.body.classList.add('modal-open');
    } else {
      document.body.classList.remove('modal-open');
    }
  }, [isModalOpen]);

  // This effect waits for the modal closing animation to finish before clearing data.
  useEffect(() => {
    let timer;
    if (!isModalOpen && selectedService) {
      timer = setTimeout(() => {
        setSelectedService(null);
      }, 400); // This duration must match the modal transition time in CSS
    }
    return () => clearTimeout(timer);
  }, [isModalOpen, selectedService]);


  return (
    <>
      <div className="page-container">
        <div className="page-content">
          <h1>Our Event Planning Services</h1>
          <div className="services-grid">
            {services.map(service => (
              <ServiceCard 
                key={service.id} 
                service={service} 
                onOpenModal={handleOpenModal} 
                isModalOpen={isModalOpen}
                isActiveCard={selectedService?.id === service.id}
              />
            ))}
          </div>
        </div>
      </div>
      <ServiceDetailModal 
        service={selectedService} 
        onClose={handleCloseModal} 
        isOpen={isModalOpen}
      />
    </>
  );
};

export default Services;
