import React, { useEffect, useRef } from 'react';
import '../styles/Footer.css';

const Footer = () => {
  const footerRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      if (!footerRef.current) return;

      const footer = footerRef.current;
      const rect = footer.getBoundingClientRect();
      const viewportHeight = window.innerHeight;

      // Start fading in when the top of the footer is within 75% of the viewport height
      if (rect.top < viewportHeight * 0.9) {
        footer.classList.add('in-view');
      }
    };

    window.addEventListener('scroll', handleScroll, { passive: true });
    // Initial check in case the footer is already in view on load
    handleScroll();

    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <footer id="SITE_FOOTER" ref={footerRef} className="site-footer">
      <div className="footer-content">
        <div className="footer-section address-section">
          <p>Unit 5, 46 Killarney Avenue, Killarney Gardens, Cape Town, South Africa, 7441</p>
        </div>

        <div className="footer-divider"></div>

        <div className="footer-section contact-section">
          <p>Tel: 021 522 9112</p>
          <a href="mailto:info@xlevents.co.za">info@xlevents.co.za</a>
        </div>

        <div className="footer-divider"></div>

        <div className="footer-section social-section">
          <div className="social-links">
            <a 
              href="https://facebook.com/EventsXL" 
              target="_blank" 
              rel="noopener noreferrer"
              className="social-link"
              aria-label="Visit our Facebook page"
            >
              <img src="https://static.wixstatic.com/media/23fd2a2be53141ed810f4d3dcdcd01fa.png/v1/fill/w_90,h_90,al_c,q_85,usm_0.66_1.00_0.01,enc_avif,quality_auto/23fd2a2be53141ed810f4d3dcdcd01fa.png" alt="Facebook" />
            </a>
            <a 
              href="https://instagram.com/xleventscapetown/" 
              target="_blank" 
              rel="noopener noreferrer"
              className="social-link"
              aria-label="Visit our Instagram page"
            >
              <img src="https://static.wixstatic.com/media/81af6121f84c41a5b4391d7d37fce12a.png/v1/fill/w_90,h_90,al_c,q_85,usm_0.66_1.00_0.01,enc_avif,quality_auto/81af6121f84c41a5b4391d7d37fce12a.png" alt="Instagram" />
            </a>
          </div>
        </div>
      </div>
      <div className="footer-bottom">
        <p className="copyright">
          Â© {new Date().getFullYear()} XL Events. All rights reserved.
        </p>
      </div>
    </footer>
  );
};

export default Footer;
