import React, { useEffect, useState, useRef, useCallback } from 'react';
import './Home.css'; // Ensure your CSS file is imported

// --- 1. The Modal Component ---
// This component is now more robust. It handles its own display, navigation,
// and crucially, all interactions with the document body (scroll lock).
const GalleryModal = ({ images, startIndex, onClose }) => {
  const [currentIndex, setCurrentIndex] = useState(startIndex);

  // Use useCallback to prevent these functions from being recreated on every render
  const handleNext = useCallback((e) => {
    e.stopPropagation(); // Prevents click from bubbling up and closing modal
    setCurrentIndex(prevIndex => (prevIndex + 1) % images.length);
  }, [images.length]);

  const handlePrev = useCallback((e) => {
    e.stopPropagation();
    setCurrentIndex(prevIndex => (prevIndex - 1 + images.length) % images.length);
  }, [images.length]);

  // This useEffect hook handles all "side effects" when the modal is open
  useEffect(() => {
    // Handler for keyboard controls
    const handleKeyDown = (e) => {
      if (e.key === 'Escape') onClose();
      if (e.key === 'ArrowRight') handleNext(e);
      if (e.key === 'ArrowLeft') handlePrev(e);
    };

    window.addEventListener('keydown', handleKeyDown);

    // --- Body Scroll Lock Logic ---
    // When the modal mounts, disable scrolling on the body
    const scrollPosition = window.scrollY;
    document.body.style.overflow = 'hidden';
    document.body.style.position = 'fixed'; // Prevents content jump
    document.body.style.top = `-${scrollPosition}px`;
    document.body.style.width = '100%';

    // This is the "cleanup" function that runs when the modal is unmounted
    return () => {
      window.removeEventListener('keydown', handleKeyDown);

      // Restore body scrolling
      document.body.style.overflow = '';
      document.body.style.position = '';
      document.body.style.top = '';
      document.body.style.width = '';
      window.scrollTo(0, scrollPosition); // Go back to where the user was
    };
  }, [onClose, handleNext, handlePrev]); // Dependencies for the effect

  // Render nothing if the image data isn't ready
  if (currentIndex === null || !images[currentIndex]) {
    return null;
  }

  // The JSX for the modal
  return (
    <div className="gallery-modal-overlay" onClick={onClose}>
      <button className="modal-close-button" onClick={onClose} aria-label="Close image view">
        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="2" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" d="M6 18L18 6M6 6l12 12" />
        </svg>
      </button>

      <button className="modal-nav-button modal-prev" onClick={handlePrev} aria-label="Previous image">
         <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="2" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" d="M15 19l-7-7 7-7" />
         </svg>
      </button>

      {/* Stop propagation here to prevent clicks on the image from closing the modal */}
      <div className="modal-image-content" onClick={(e) => e.stopPropagation()}>
        <img src={images[currentIndex]} alt={`Full screen view of event ${currentIndex + 1}`} />
      </div>

      <button className="modal-nav-button modal-next" onClick={handleNext} aria-label="Next image">
         <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="2" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" d="M9 5l7 7-7 7" />
         </svg>
      </button>
    </div>
  );
};


// --- 2. The Image Carousel Component ---
// This component now correctly handles opening the modal and applying the blur effect.
const ImageCarousel = ({ images }) => {
  const [isPaused, setIsPaused] = useState(false);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedImageIndex, setSelectedImageIndex] = useState(null);

  // This function is called when any image in the carousel is clicked
  const handleImageClick = (index) => {
    setSelectedImageIndex(index % images.length); // Ensures index is always valid
    setIsModalOpen(true); // This state change triggers the modal to appear

    // Safely find the root element and apply the blur class
    const appRoot = document.getElementById('root');
    if (appRoot) {
      appRoot.classList.add('body-blur');
    }
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setSelectedImageIndex(null);

    // Safely find the root element and remove the blur class
    const appRoot = document.getElementById('root');
    if (appRoot) {
      appRoot.classList.remove('body-blur');
    }
  };

  const imageList = [...images, ...images]; // For seamless scrolling animation

  return (
    <>
      <div
        className="carousel-container"
        onMouseEnter={() => setIsPaused(true)}
        onMouseLeave={() => setIsPaused(false)}
      >
        <div className={`carousel-track top-track ${isPaused ? 'paused' : ''}`}>
          {imageList.map((img, index) => (
            <div className="carousel-card" key={`top-${index}`} onClick={() => handleImageClick(index)}>
              <img src={img} alt={`Gallery of successful team events ${index + 1}`} />
            </div>
          ))}
        </div>
        <div className={`carousel-track bottom-track ${isPaused ? 'paused' : ''}`}>
          {imageList.map((img, index) => (
            <div className="carousel-card" key={`bottom-${index}`} onClick={() => handleImageClick(index)}>
              <img src={img} alt={`Gallery of corporate culture workshops ${index + 1}`} />
            </div>
          ))}
        </div>
      </div>

      {/* Conditionally render the modal component based on state */}
      {isModalOpen && (
        <GalleryModal 
          images={images} 
          startIndex={selectedImageIndex} 
          onClose={handleCloseModal} 
        />
      )}
    </>
  );
};


