import React, { useEffect, useState, useRef, useCallback } from 'react';
import '.styles/Home.css';

// ImageCarousel Component
const ImageCarousel = ({ images }) => {
  const [isPaused, setIsPaused] = useState(false);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedImageIndex, setSelectedImageIndex] = useState(null);

  // Function to handle clicking an image
  const handleImageClick = (index) => {
    setSelectedImageIndex(index);
    setIsModalOpen(true);
  };

  // Function to close the modal
  const handleCloseModal = () => {
    setIsModalOpen(false);
    setSelectedImageIndex(null);
  };

  // Function to navigate to next image
  const handleNextImage = (e) => {
    e.stopPropagation();
    setSelectedImageIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  // Function to navigate to previous image
  const handlePrevImage = (e) => {
    e.stopPropagation();
    setSelectedImageIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length);
  };

  // Handle keyboard navigation
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (!isModalOpen) return;

      if (e.key === 'Escape') {
        handleCloseModal();
      } else if (e.key === 'ArrowRight') {
        setSelectedImageIndex((prevIndex) => (prevIndex + 1) % images.length);
      } else if (e.key === 'ArrowLeft') {
        setSelectedImageIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length);
      }
    };

    if (isModalOpen) {
      document.addEventListener('keydown', handleKeyDown);
    }

    return () => {
      document.removeEventListener('keydown', handleKeyDown);
    };
  }, [isModalOpen, images.length]);

  // Effect to add/remove a blur class to the main app container when modal opens/closes
  useEffect(() => {
    const appRoot = document.getElementById('root');
    if (appRoot) {
      if (isModalOpen) {
        appRoot.classList.add('body-blur');
        document.body.style.overflow = 'hidden';
      } else {
        appRoot.classList.remove('body-blur');
        document.body.style.overflow = 'unset';
      }
    }

    // Cleanup function to ensure the class is removed if the component unmounts
    return () => {
      if (appRoot) {
        appRoot.classList.remove('body-blur');
        document.body.style.overflow = 'unset';
      }
    };
  }, [isModalOpen]);

  // We duplicate the images to create a seamless, infinite loop effect
  const imageList = [...images, ...images];

  return (
    <div className="carousel-container" onMouseEnter={() => setIsPaused(true)} onMouseLeave={() => setIsPaused(false)}>
      {/* Top Row */}
      <div className={`carousel-track top-track ${isPaused ? 'paused' : ''}`}>
        {imageList.map((img, index) => (
          <div className="carousel-card" key={`top-${index}`} onClick={() => handleImageClick(index % images.length)}>
            <img src={img} alt={`Gallery of successful team events ${index + 1}`} />
          </div>
        ))}
      </div>

      {/* Bottom Row */}
      <div className={`carousel-track bottom-track ${isPaused ? 'paused' : ''}`}>
        {imageList.map((img, index) => (
          <div className="carousel-card" key={`bottom-${index}`} onClick={() => handleImageClick(index % images.length)}>
            <img src={img} alt={`Gallery of corporate culture workshops ${index + 1}`} />
          </div>
        ))}
      </div>

      {/* Modal/Lightbox for full-screen image view */}
      {isModalOpen && selectedImageIndex !== null && (
        <div className="carousel-modal" onClick={handleCloseModal}>
          <button className="modal-close-button" onClick={handleCloseModal} aria-label="Close image view">
            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="2" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" d="M6 18L18 6M6 6l12 12" />
            </svg>
          </button>

          <button className="modal-nav-button modal-prev" onClick={handlePrevImage} aria-label="Previous image">
            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="2" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" d="M15 19l-7-7 7-7" />
            </svg>
          </button>

          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <img src={images[selectedImageIndex]} alt="Full screen view of event" />
          </div>

          <button className="modal-nav-button modal-next" onClick={handleNextImage} aria-label="Next image">
            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="2" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" d="M9 5l7 7-7 7" />
            </svg>
          </button>
        </div>
      )}
    </div>
  );
};

// Main Home Component
const Home = () => {
  const [scrollY, setScrollY] = useState(0);
  const testimonialsScrollerRef = useRef(null);

  // Stock images for the carousel
  const galleryImages = [
    "https://images.unsplash.com/photo-1511795409834-ef04bbd61622?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
    "https://images.unsplash.com/photo-1530103862676-de8c9debad1d?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
    "https://images.unsplash.com/photo-1519167758481-83f29b1fe26d?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
    "https://images.unsplash.com/photo-1464366400600-7168b8af9bc3?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
    "https://images.unsplash.com/photo-1558618666-fcd25c85cd64?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
    "https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
    "https://images.unsplash.com/photo-1492684223066-81342ee5ff30?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
    "https://images.unsplash.com/photo-1523580494863-6f3031224c94?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80"
  ];

  // Hero background image
  const heroImage = "https://images.unsplash.com/photo-1511795409834-ef04bbd61622?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1920&q=80";

  // Services section image
  const servicesImage = "https://images.unsplash.com/photo-1530103862676-de8c9debad1d?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1920&q=80";

  // Dummy testimonials
  const testimonials = [
    {
      quote: "Working with this team was an absolute dream. They took our vision and brought it to life with such elegance and precision. Our wedding day was perfect.",
      author: "Sarah & Tom",
      event: "Wedding Celebration"
    },
    {
      quote: "The professionalism and creativity are unmatched. They handled our annual corporate gala, and the feedback from attendees was overwhelmingly positive. Flawlessly executed.",
      author: "Johnathan Lee",
      event: "CEO, Innovate Corp."
    },
    {
      quote: "From the initial consultation to the day of the event, the communication was seamless. They are true experts, creating a magical atmosphere for our anniversary.",
      author: "Maria Garcia",
      event: "50th Anniversary Party"
    },
    {
        quote: "I was truly impressed by their attention to detail. Every element of our product launch was thoughtfully considered and beautifully implemented. A five-star experience.",
        author: "Aisha Khan",
        event: "Product Launch Event"
    },
    {
        quote: "They transformed our charity fundraiser into an unforgettable evening. Their passion and dedication shone through, helping us exceed our fundraising goals.",
        author: "David Chen",
        event: "Director, Unity Foundation"
    },
    {
        quote: "An incredible team that is both organized and inspired. They took all the stress out of planning and allowed us to simply enjoy our special day. Highly recommend!",
        author: "Emily & Mark",
        event: "Private Estate Wedding"
    }
  ];

  // Dummy client logos
  const clientLogos = [
    'https://logodix.com/logo/1722880.png',
    'https://logodix.com/logo/1722880.png',
    'https://logodix.com/logo/1722880.png',
    'https://logodix.com/logo/1722880.png',
    'https://logodix.com/logo/1722880.png',
    'https://logodix.com/logo/1722880.png',
  ];

  // Function to handle scrolling testimonials
  const handleTestimonialScroll = () => {
    if (testimonialsScrollerRef.current) {
        const scroller = testimonialsScrollerRef.current;
        const cardWidth = 360 + 32; // card width + margin-right in pixels
        const newScrollLeft = scroller.scrollLeft + cardWidth;

        if (newScrollLeft >= scroller.scrollWidth - scroller.clientWidth) {
            scroller.scrollTo({ left: 0, behavior: 'smooth' });
        } else {
            scroller.scrollTo({ left: newScrollLeft, behavior: 'smooth' });
        }
    }
  };

  useEffect(() => {
    const handleScroll = () => {
      setScrollY(window.scrollY);
    };

    window.addEventListener('scroll', handleScroll);

    const sections = document.querySelectorAll('.services-section, .testimonials-section');
    const observer = new IntersectionObserver((entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          entry.target.classList.add('in-view');
        }
      });
    }, { threshold: 0.1 });

    sections.forEach(section => observer.observe(section));

    return () => {
      window.removeEventListener('scroll', handleScroll);
      sections.forEach(section => observer.unobserve(section));
    };
  }, []);

  const parallaxStyle = {
    transform: `translate3d(0, ${scrollY * 0.4}px, 0)`
  };

  return (
    <div className="home-page-clone">
      {/* --- HERO SECTION --- */}
      <section className="hero-section">
        <div className="parallax-background" style={{ ...parallaxStyle, backgroundImage: `url(${heroImage})` }}></div>
        <div className="hero-pattern-overlay"></div>
        <div className="hero-content">
          <div className="hero-text-content">
            <h1 className="hero-main-title">Timeless</h1>
            <h2 className="hero-subtitle">Events</h2>
            <h3 className="hero-accent">Crafted with Passion</h3>
          </div>
        </div>
        <a href="#services" className="hero-scroll-indicator" aria-label="Scroll down">
          <div className="scroll-dot"></div>
        </a>
      </section>

      {/* --- SERVICES SECTION --- */}
      <section id="services" className="services-section">
        <div className="services-text-column">
          <div className="services-content">
            <h2>Our Services</h2>
            <div className="services-icon">
              <svg className="floral-ornament" viewBox="0 0 120 40" xmlns="http://www.w3.org/2000/svg">
                <path d="M10 20 Q30 5 50 20 Q70 35 90 20 Q110 5 120 20" stroke="#8b7355" strokeWidth="2" fill="none"/>
                <circle cx="30" cy="15" r="3" fill="#8b7355"/>
                <circle cx="60" cy="20" r="4" fill="#8b7355"/>
                <circle cx="90" cy="15" r="3" fill="#8b7355"/>
              </svg>
            </div>
            <p>From intimate gatherings to grand celebrations, we specialize in creating unforgettable experiences tailored to your unique vision.</p>
            <a href="/services" className="discover-button">Discover More</a>
          </div>
        </div>
        <div className="services-image-column" style={{ backgroundImage: `url(${servicesImage})` }}></div>
      </section>

      {/* --- TESTIMONIALS SECTION --- */}
      <section className="testimonials-section">
          <div className="testimonials-header">
              <h2>What Our Clients Say</h2>
              <div className="quote-icon">"</div>
          </div>
          <div className="testimonials-container">
              <div className="testimonials-scroller" ref={testimonialsScrollerRef}>
                  <div className="testimonials-grid">
                      {testimonials.map((testimonial, index) => (
                          <div className="testimonial-card" key={index}>
                              <p className="testimonial-quote">{testimonial.quote}</p>
                              <div className="testimonial-author">
                                  {testimonial.author}
                                  <span>{testimonial.event}</span>
                              </div>
                          </div>
                      ))}
                  </div>
              </div>
              <button 
                  className="testimonials-arrow-right" 
                  onClick={handleTestimonialScroll} 
                  aria-label="Scroll testimonials right"
              >
                  <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="2" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" d="M9 5l7 7-7 7" />
                  </svg>
              </button>
          </div>
      </section>

      {/* --- CLIENT LOGO RIBBON --- */}
      <section className="client-logo-ribbon">
        <div className="logo-track-wrapper">
          <div className="logo-track">
              {[...clientLogos, ...clientLogos].map((logo, index) => (
                  <img key={index} src={logo} alt={`Client logo ${index + 1}`} className="client-logo" />
              ))}
          </div>
        </div>
      </section>

      {/* --- GALLERY CAROUSEL SECTION --- */}
      <section className="gallery-carousel-section">
        <div className="gallery-carousel-header">
          <h2>Gallery of Success</h2>
          <p>Explore a curated collection of our most memorable events, each telling a unique story of collaboration and celebration.</p>
        </div>

        <ImageCarousel images={galleryImages} />

        <div className="gallery-carousel-cta">
          <a href="/gallery" className="discover-button-dark">View Full Gallery</a>
        </div>
      </section>
    </div>
  );
};

export default Home;
