import React, { useState } from 'react';
import '../styles/Footer.css';

const Footer = () => {
  const [email, setEmail] = useState('');
  const [formState, setFormState] = useState({ submitting: false, success: false, error: false });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setFormState({ submitting: true, success: false, error: false });

    try {
      const response = await fetch('https://formspree.io/f/xdkzdoka', { // <-- PASTE YOUR URL HERE
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email }),
      });

      if (response.ok) {
        setFormState({ submitting: false, success: true, error: false });
        setEmail('');
      } else {
        throw new Error('Failed to submit form');
      }
    } catch (error) {
      console.error('Form submission error:', error);
      setFormState({ submitting: false, success: false, error: true });
    }
  };


  return (
    <footer id="SITE_FOOTER" className="site-footer">
      <div className="footer-container">
        {/* Top Section: Newsletter Signup */}
        <div className="footer-top-section">
          <div className="footer-signup-text">
            <h2>Reach out to RDS for your next event</h2>

          </div>
          <div className="footer-signup-form-container">
            <form className="footer-signup-form" onSubmit={handleSubmit}>
              <div className="form-input-wrapper">
                <input
                  type="email"
                  name="email"
                  placeholder="Your email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                  disabled={formState.submitting}
                />
                <button type="submit" disabled={formState.submitting}>
                  {formState.submitting ? 'Sending...' : 'Send'}
                </button>
              </div>
            </form>
            {formState.success && <p className="form-disclaimer success-message">Thank you! You've been subscribed.</p>}
            {formState.error && <p className="form-disclaimer error-message">Something went wrong. Please try again.</p>}
            {!formState.success && !formState.error && (
                <p className="form-disclaimer">
                    By clicking send, you'll receive Product Insight updates from Jimo. You always have the choice to unsubscribe from every email you receive.
                </p>
            )}
          </div>
        </div>

        {/* Divider */}
        <div className="footer-divider-full"></div>

        {/* Bottom Section: Links */}
        <div className="footer-bottom-section">
          <div className="footer-logo-column">
            <h3 className="footer-jimo-logo">RDS</h3>
          </div>
          <div className="footer-links-grid">
            <div className="footer-link-column">
              <h4>FEATURES</h4>
              <ul>
                <li><a href="#">Product Tours</a></li>
                <li><a href="#">Jimo AI</a></li>
              </ul>
            </div>
            <div className="footer-link-column">
              <h4>EXPLORE</h4>
              <ul>
                <li><a href="#">Pricing</a></li>
                <li><a href="#">Integrations</a></li>
                <li><a href="#" className="whats-new">What's new <span className="red-dot"></span></a></li>
                <li><a href="#">Blog</a></li>
                <li><a href="#">Laws of Onboarding</a></li>
                <li><a href="#">Installation</a></li>
                <li><a href="#">Feedback</a></li>
                <li><a href="#">Documentation</a></li>
              </ul>
            </div>
            <div className="footer-link-column">
              <h4>COMPANY</h4>
              <ul>
                <li><a href="#">Book a Demo</a></li>
                <li><a href="#">LinkedIn</a></li>
                <li><a href="#">Twitter</a></li>
                <li><a href="#">Product Hunt</a></li>
                <li><a href="#">Jimo on G2</a></li>
              </ul>
            </div>
            <div className="footer-link-column">
              <h4>COMPARISON</h4>
              <ul>
                <li><a href="#">Jimo VS Userpilot</a></li>
                <li><a href="#">Jimo VS Appcues</a></li>
              </ul>
            </div>
            <div className="footer-link-column">
              <h4>LEGAL</h4>
              <ul>
                <li><a href="#">Terms of Services</a></li>
                <li><a href="#">Privacy</a></li>
                <li><a href="#">Cookie Policy</a></li>
              </ul>
              <div className="backed-by">
                <p>backed by</p>
                <p className="techstars-logo">techstars</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
