import React from 'react';

const Card = ({ card, index, onCardClick }) => {
  // The --index variable is used by CSS to create the stacked effect
  const style = {
    '--index': index,
  };

  return (
    <li
      className="card"
      style={style}
      // This onClick is now only for opening the modal, and is only active when the row is expanded.
      onClick={onCardClick}
    >
      <img src={card.imageUrl} alt={card.title} />
    </li>
  );
};

export default Card;
