import React, { useState, useEffect } from 'react';

const ServiceCard = ({ service, onOpenModal, isModalOpen, isActiveCard }) => {
  const [isAnimated, setIsAnimated] = useState(false); // Controls the lift/hover state

  // This hook ensures the card returns to its initial state when the modal is closed.
  useEffect(() => {
    // If the modal is no longer open and this card was the one that was active...
    if (!isModalOpen && isActiveCard) {
      // ...then reset its animation state.
      setIsAnimated(false);
    }
  }, [isModalOpen, isActiveCard]);

  // --- REBUILT INTERACTION LOGIC ---

  // 1. CLICKING ANYWHERE ON THE CARD: Lifts the card and shows details.
  const handleCardClick = () => {
    setIsAnimated(true);
  };

  // 2. CLICKING THE "VIEW DETAILS" BUTTON: Opens the modal.
  const handleViewDetailsClick = (e) => {
    e.stopPropagation(); // VERY IMPORTANT: Prevents the card's onClick from also firing.
    onOpenModal(service);
  };

  // Mouse events for desktop users
  const handleMouseEnter = () => setIsAnimated(true);
  const handleMouseLeave = () => setIsAnimated(false);

  return (
    <div 
        id="make-3D-space" 
        // We use onMouseDown for click to feel slightly more responsive than onClick
        onMouseDown={handleCardClick}
        onMouseEnter={handleMouseEnter}
        onMouseLeave={handleMouseLeave}
    >
      <div
        id="product-card"
        className={isAnimated ? 'animate' : ''}
      >
        <div id="product-front">
          <img src={service.image} alt={service.title} />
          <div className="image_overlay"></div>

          <div className="card-title-container">
            <h2>{service.title}</h2>
          </div>

          <div id="view_details" onMouseDown={handleViewDetailsClick}>
            View Details
          </div>

          <div className="stats">
            <div className="stats-container">
              <span className="product_name">{service.summaryTitle}</span>
              <p>{service.summaryDescription}</p>
              <div className="product-options">
                {service.summaryOptions.map((opt, index) => (
                    <div key={index}>
                        <strong>{opt.type}</strong>
                        <span>{opt.details}</span>
                    </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ServiceCard;
