import React, { useState, useRef } from 'react';

const ServiceCard = ({ service }) => {
  const [cardClasses, setCardClasses] = useState('');
  const [isFlipped, setIsFlipped] = useState(false);
  const cardRef = useRef(null);

  const handleMouseEnter = () => {
    if (!isFlipped) {
      setCardClasses('animate');
    }
  };

  const handleMouseLeave = () => {
    if (!isFlipped) {
      setCardClasses('');
    }
  };

  const handleViewDetails = () => {
    setIsFlipped(true);

    let newClasses = 'flip-10';
    setCardClasses(newClasses);

    setTimeout(() => {
      newClasses = 'flip-10 flip90';
      setCardClasses(newClasses);
      cardRef.current.querySelector('#product-front').style.display = 'none';
    }, 50);

    setTimeout(() => {
      newClasses = 'flip-10 flip90 flip190';
      setCardClasses(newClasses);
      cardRef.current.querySelector('#product-back').style.display = 'block';
    }, 150);

    setTimeout(() => {
      newClasses = 'flip-10 flip90 flip190 flip180';
      setCardClasses(newClasses);
    }, 250);

    setTimeout(() => {
        // Animate the 'X'
        cardRef.current.querySelector('#cx').classList.add('s1', 's2', 's3');
        cardRef.current.querySelector('#cy').classList.add('s1', 's2', 's3');
    }, 400);

  };

  const handleFlipBack = () => {
    setIsFlipped(false);

    let newClasses = 'flip-10 flip90 flip190';
    setCardClasses(newClasses);

    setTimeout(() => {
        cardRef.current.querySelector('#cx').classList.remove('s1', 's2', 's3');
        cardRef.current.querySelector('#cy').classList.remove('s1', 's2', 's3');
    }, 100)

    setTimeout(() => {
        newClasses = 'flip-10 flip90';
        setCardClasses(newClasses);
        cardRef.current.querySelector('#product-back').style.display = 'none';
    }, 150);

    setTimeout(() => {
        newClasses = 'flip-10';
        setCardClasses(newClasses);
        cardRef.current.querySelector('#product-front').style.display = 'block';
    }, 250);

    setTimeout(() => {
        newClasses = '';
        setCardClasses(newClasses);
    }, 400);
  };

  return (
    <div id="make-3D-space">
      <div 
        id="product-card" 
        ref={cardRef}
        className={cardClasses}
        onMouseEnter={handleMouseEnter}
        onMouseLeave={handleMouseLeave}
      >
        <div id="product-front">
          <div className="shadow"></div>
          <img src={service.image} alt={service.title} />
          <div className="image_overlay"></div>
          <div id="view_details" onClick={handleViewDetails}>View details</div>
          <div className="stats">
            <div className="stats-container">
              <span className="product_name">{service.title}</span>
              <p>{service.shortDescription}</p>
            </div>
          </div>
        </div>
        <div id="product-back" style={{ display: 'none' }}>
          <div className="shadow"></div>
          <div id="flip-back" onClick={handleFlipBack}>
            <div id="cy"></div>
            <div id="cx"></div>
          </div>
          <div className="back-content">
              <h2>{service.title}</h2>
              <p>{service.fullDescription}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ServiceCard;
