          transition: transform 0.4s cubic-bezier(0.165, 0.84, 0.44, 1), box-shadow 0.4s cubic-bezier(0.165, 0.84, 0.44, 1);
          cursor: pointer;
          border: 1px solid rgba(0, 0, 0, 0.05);
        }

        .carousel-card:hover {
          transform: scale(1.05) translateY(-5px);
          box-shadow: 0 12px 24px rgba(0, 0, 0, 0.15);
        }

        .carousel-card img {
          width: 100%;
          height: 100%;
          object-fit: cover;
        }

        /* Global class to apply blur to the main app container */
        #root.body-blur > * {
          filter: blur(10px) brightness(0.6);
          transition: filter 0.4s ease-out;
          pointer-events: none; 
        }

        /* The modal must not be blurred */
        #root.body-blur .carousel-modal {
            filter: none;
            pointer-events: auto;
        }

        /* Full-screen Modal/Lightbox */
        .carousel-modal {
          position: fixed;
          top: 0;
          left: 0;
          width: 100vw;
          height: 100vh;
          display: flex;
          align-items: center;
          justify-content: center;
          z-index: 2000;
          background-color: transparent;
          padding: 2rem;
          box-sizing: border-box;
        }

        .modal-close-button {
          position: absolute;
          top: 2rem;
          right: 2rem;
          background: rgba(0, 0, 0, 0.3);
          border: none;
          border-radius: 50%;
          width: 48px;
          height: 48px;
          cursor: pointer;
          display: flex;
          align-items: center;
          justify-content: center;
          color: white;
          transition: background-color 0.3s ease, transform 0.3s ease;
          z-index: 2001;
        }

        .modal-close-button:hover {
          background: rgba(0, 0, 0, 0.5);
          transform: rotate(90deg);
        }

        .modal-close-button svg {
          width: 28px;
          height: 28px;
        }

        .modal-content {
          max-width: 90vw;
          max-height: 90vh;
          animation: zoomIn 0.4s cubic-bezier(0.165, 0.84, 0.44, 1);
        }

        .modal-content img {
          width: 100%;
          height: 100%;
          max-width: 100%;
          max-height: 90vh;
          object-fit: contain;
          border-radius: 12px;
          box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.5);
        }

        @keyframes zoomIn {
          from { transform: scale(0.8); opacity: 0; }
          to { transform: scale(1); opacity: 1; }
        }

        /* --- RESPONSIVE STYLES --- */
        @media (max-width: 900px) {
          .services-section { flex-direction: column-reverse; }
          .services-text-column, .services-image-column { width: 100%; min-height: 400px; }
          .services-text-column { padding: 3rem 2rem; }
