import React, { useEffect, useState } from 'react';

// ImageCarousel Component
const ImageCarousel = ({ images }) => {
  const [isPaused, setIsPaused] = useState(false);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);

  // Function to handle clicking an image
  const handleImageClick = (imageUrl) => {
    setSelectedImage(imageUrl);
    setIsModalOpen(true);
  };

  // Function to close the modal
  const handleCloseModal = () => {
    setIsModalOpen(false);
    setSelectedImage(null);
  };

  // Effect to add/remove a blur class to the main app container when modal opens/closes
  useEffect(() => {
    const appRoot = document.getElementById('root');
    if (appRoot) {
      if (isModalOpen) {
        appRoot.classList.add('body-blur');
      } else {
        appRoot.classList.remove('body-blur');
      }
    }

    // Cleanup function to ensure the class is removed if the component unmounts
    return () => {
      if (appRoot) {
        appRoot.classList.remove('body-blur');
      }
    };
  }, [isModalOpen]);

  // We duplicate the images to create a seamless, infinite loop effect
  const imageList = [...images, ...images];

  return (
    <div
      className="carousel-container"
      onMouseEnter={() => setIsPaused(true)}
      onMouseLeave={() => setIsPaused(false)}
    >
      {/* Top Row */}
      <div className={`carousel-track top-track ${isPaused ? 'paused' : ''}`}>
        {imageList.map((img, index) => (
          <div className="carousel-card" key={`top-${index}`} onClick={() => handleImageClick(img)}>
            <img src={img} alt={`Gallery of successful team events ${index + 1}`} />
          </div>
        ))}
      </div>

      {/* Bottom Row (moves in the opposite direction) */}
      <div className={`carousel-track bottom-track ${isPaused ? 'paused' : ''}`}>
        {imageList.map((img, index) => (
          <div className="carousel-card" key={`bottom-${index}`} onClick={() => handleImageClick(img)}>
            <img src={img} alt={`Gallery of corporate culture workshops ${index + 1}`} />
          </div>
        ))}
      </div>

      {/* Modal/Lightbox for full-screen image view */}
      {isModalOpen && (
        <div className="carousel-modal" onClick={handleCloseModal}>
          <button className="modal-close-button" onClick={handleCloseModal} aria-label="Close image view">
            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="2" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" d="M6 18L18 6M6 6l12 12" />
            </svg>
          </button>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <img src={selectedImage} alt="Full screen view of event" />
          </div>
        </div>
      )}
    </div>
  );
};

// Main Home Component
const Home = () => {
  const [scrollY, setScrollY] = useState(0);

  // Stock images for the carousel
  const galleryImages = [
    "https://images.unsplash.com/photo-1511795409834-ef04bbd61622?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
    "https://images.unsplash.com/photo-1530103862676-de8c9debad1d?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
    "https://images.unsplash.com/photo-1519167758481-83f29b1fe26d?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
    "https://images.unsplash.com/photo-1464366400600-7168b8af9bc3?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
    "https://images.unsplash.com/photo-1558618666-fcd25c85cd64?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
    "https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
    "https://images.unsplash.com/photo-1492684223066-81342ee5ff30?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
    "https://images.unsplash.com/photo-1523580494863-6f3031224c94?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80"
  ];

  // Hero background image
  const heroImage = "https://images.unsplash.com/photo-1511795409834-ef04bbd61622?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1920&q=80";

  // Services section image
  const servicesImage = "https://images.unsplash.com/photo-1530103862676-de8c9debad1d?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1920&q=80";

  // Dummy testimonials - now with 6 cards
  const testimonials = [
    {
      quote: "Working with this team was an absolute dream. They took our vision and brought it to life with such elegance and precision. Our wedding day was perfect.",
      author: "Sarah & Tom",
      event: "Wedding Celebration"
    },
    {
      quote: "The professionalism and creativity are unmatched. They handled our annual corporate gala, and the feedback from attendees was overwhelmingly positive. Flawlessly executed.",
      author: "Johnathan Lee",
      event: "CEO, Innovate Corp."
    },
    {
      quote: "From the initial consultation to the day of the event, the communication was seamless. They are true experts, creating a magical atmosphere for our anniversary.",
      author: "Maria Garcia",
      event: "50th Anniversary Party"
    },
    {
        quote: "I was truly impressed by their attention to detail. Every element of our product launch was thoughtfully considered and beautifully implemented. A five-star experience.",
        author: "Aisha Khan",
        event: "Product Launch Event"
    },
    {
        quote: "They transformed our charity fundraiser into an unforgettable evening. Their passion and dedication shone through, helping us exceed our fundraising goals.",
        author: "David Chen",
        event: "Director, Unity Foundation"
    },
    {
        quote: "An incredible team that is both organized and inspired. They took all the stress out of planning and allowed us to simply enjoy our special day. Highly recommend!",
        author: "Emily & Mark",
        event: "Private Estate Wedding"
    }
  ];

  // Dummy client logos
  const clientLogos = [
    'https://logodix.com/logo/1722880.png',
    'https://logodix.com/logo/1722880.png',
    'https://logodix.com/logo/1722880.png',
    'https://logodix.com/logo/1722880.png',
    'https://logodix.com/logo/1722880.png',
    'https://logodix.com/logo/1722880.png',
  ];


  useEffect(() => {
    const handleScroll = () => {
      setScrollY(window.scrollY);
    };

    window.addEventListener('scroll', handleScroll);

    // Intersection Observer for fade-in animations
    const sections = document.querySelectorAll('.services-section, .testimonials-section');
    const observer = new IntersectionObserver((entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          entry.target.classList.add('in-view');
        }
      });
    }, { threshold: 0.1 });

    sections.forEach(section => observer.observe(section));

    return () => {
      window.removeEventListener('scroll', handleScroll);
      sections.forEach(section => observer.unobserve(section));
    };
  }, []);

  const parallaxStyle = {
    transform: `translate3d(0, ${scrollY * 0.4}px, 0)`
  };

  return (
    <>
      <style>{`
        /* --- FONT IMPORT --- */
        @import url('https://fonts.googleapis.com/css2?family=EB+Garamond:wght@400;500&family=Inter:wght@400;500;600&display=swap');

        /* --- GLOBAL STYLES --- */
        .home-page-clone {
          background-color: #ffffff;
          color: #101010;
        }

        .home-page-clone section {
          width: 100%;
          position: relative;
        }

        /* --- SECTION 1: SERVICES --- */
        .services-section {
          display: flex;
          background-color: #f8f6f3;
          position: relative;
        }

        .services-text-column, .services-image-column {
          width: 50%;
          min-height: 500px;
        }

        .services-text-column {
          display: flex;
          align-items: center;
          justify-content: center;
          padding: 4rem 10%;
          background-color: #f0ebe5;
          position: relative;
          margin-top: 65px;
          border: 3rem solid #ffffff;
          box-sizing: border-box;
        }

        .services-text-column::after {
          content: '';
          position: absolute;
          top: 40px;
          right: 0;
          bottom: 40px;
          width: 1.5px;
          background-color: #ede6e6;
        }

        .services-content {
          text-align: center;
          color: #333;
          max-width: 400px;
        }

        .services-content h2 {
          font-family: 'Inter', sans-serif;
          font-weight: 400;
          font-size: 1.1rem;
          letter-spacing: 0.4em;
          color: #8b7355;
          text-transform: uppercase;
          margin-bottom: 1.5rem;
        }

        .services-icon {
          margin: 2rem auto 2.5rem;
          display: flex;
          justify-content: center;
        }

        .floral-ornament {
          width: 120px;
          height: 40px;
        }

        .services-content p {
          font-family: 'EB Garamond', serif;
          font-size: 1.1rem;
          font-weight: 400;
          line-height: 1.6;
          color: #6b5b4d;
          margin-bottom: 2.5rem;
        }

        .discover-button {
          display: inline-block;
          padding: 14px 40px;
          border: 1.5px solid #8b7355;
          color: #8b7355;
          background-color: transparent;
          font-family: 'Inter', sans-serif;
          font-size: 0.75rem;
          font-weight: 500;
          letter-spacing: 0.15em;
          text-decoration: none;
          text-transform: uppercase;
          transition: all 0.3s ease;
        }

        .discover-button:hover {
          background-color: #8b7355;
          color: white;
          transform: translateY(-1px);
        }

        .services-image-column {
          background-size: cover;
          background-position: center;
          border: 3rem solid #ffffff;
          box-sizing: border-box;
          background-attachment: fixed;
          opacity: 0;
          transition: opacity 1s 0.2s ease-in-out;
          margin-top: 65px;
        }

        .services-section.in-view .services-image-column {
          opacity: 1;
        }

        /* --- SECTION 2: HERO --- */
        .hero-section {
          height: 100vh;
          display: flex;
          align-items: center;
          justify-content: center;
          text-align: center;
          color: white;
          position: relative;
          border: none;
          margin: 0;
          padding-top: 100px;
          box-sizing: border-box;
          overflow: hidden;
        }

        .parallax-background {
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 140vh;
          background-size: cover;
          background-position: center;
          z-index: 1;
          will-change: transform;
        }

        .hero-pattern-overlay {
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          z-index: 2;
          background: linear-gradient(135deg, rgba(0,0,0,0.2) 0%, rgba(0,0,0,0.4) 50%, rgba(0,0,0,0.3) 100%);
          border: 2px solid rgba(255, 255, 255, 0.8);
          margin: 0;
        }

        .hero-content {
          position: relative;
          z-index: 3;
          max-width: 900px;
          padding: 0 2rem;
          width: 100%;
          height: 100%;
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
        }

        .hero-text-content {
          margin-bottom: 4rem;
          padding-top: 2rem;
        }

        .hero-text-content .hero-main-title {
          font-family: 'EB Garamond', serif;
          font-size: clamp(3rem, 8vw, 6rem);
          font-weight: 400;
          letter-spacing: 0.2em;
          text-transform: uppercase;
          margin: 0 0 -0.5rem 0;
          line-height: 0.9;
          text-shadow: 0 4px 20px rgba(0,0,0,0.3);
          color: white !important;
        }

        .hero-text-content .hero-subtitle {
          font-family: 'EB Garamond', serif;
          font-size: clamp(2rem, 5vw, 3.5rem);
          font-weight: 300;
          letter-spacing: 0.15em;
          margin: 0 0 -0.3rem 0;
          line-height: 1;
          text-shadow: 0 3px 15px rgba(0,0,0,0.3);
          color: white !important;
        }

        .hero-text-content .hero-accent {
          font-family: 'EB Garamond', serif;
          font-size: clamp(1.8rem, 4vw, 3rem);
          font-weight: 500;
          font-style: italic;
          letter-spacing: 0.1em;
          margin: 0;
          opacity: 0.95;
          text-shadow: 0 2px 10px rgba(0,0,0,0.3);
          color: white !important;
        }

        .hero-scroll-indicator {
          position: absolute;
          bottom: 3rem;
          left: 50%;
          transform: translateX(-50%);
          display: flex;
          flex-direction: column;
          align-items: center;
          text-decoration: none;
          cursor: pointer;
        }

        .scroll-dot {
          width: 8px;
          height: 8px;
          border: 2px solid white;
          border-radius: 50%;
          background: transparent;
          animation: scrollPulse 2s infinite ease-in-out;
        }

        .scroll-dot::after {
          content: '';
          position: absolute;
          top: 15px;
          left: 50%;
          transform: translateX(-50%);
          width: 1px;
          height: 30px;
          background: rgba(255, 255, 255, 0.6);
        }

        @keyframes scrollPulse {
          0%, 100% { opacity: 0.6; transform: scale(1); }
          50% { opacity: 1; transform: scale(1.2); }
        }

        /* --- SECTION 3: TESTIMONIALS (Horizontally Scrolling) --- */
        .testimonials-section {
            background-color: #f8f6f3;
            padding: 8rem 0;
            overflow: hidden;
        }

        .testimonials-header {
            max-width: 800px;
            margin: 0 auto 4rem auto;
            text-align: center;
            padding: 0 2rem;
        }

        .testimonials-header h2 {
            font-family: 'Inter', sans-serif;
            font-weight: 400;
            font-size: 1.1rem;
            letter-spacing: 0.4em;
            color: #8b7355;
            text-transform: uppercase;
            margin-bottom: 1rem;
        }

        .testimonials-header .quote-icon {
            color: #8b7355;
            font-size: 3rem;
            font-family: 'EB Garamond', serif;
            line-height: 1;
        }

        .testimonials-scroller {
            overflow-x: auto;
            padding-bottom: 2rem; /* space for scrollbar */
            scrollbar-width: thin;
            scrollbar-color: #c1b6a7 #f8f6f3;
        }

        .testimonials-scroller::-webkit-scrollbar {
            height: 8px;
        }

        .testimonials-scroller::-webkit-scrollbar-track {
            background: #f8f6f3;
            border-radius: 4px;
        }

        .testimonials-scroller::-webkit-scrollbar-thumb {
            background-color: #c1b6a7;
            border-radius: 4px;
        }

        .testimonials-grid {
            display: flex;
            flex-wrap: nowrap;
            padding-left: max(2rem, (100% - 1200px) / 2);
            padding-right: max(2rem, (100% - 1200px) / 2);
            width: fit-content; /* Important for flex scrolling */
        }

        .testimonial-card {
            background-color: #ffffff;
            padding: 2.5rem;
            border-radius: 8px;
            box-shadow: 0 10px 25px rgba(0,0,0,0.05);
            border: 1px solid #edeae6;
            text-align: left;
            flex: 0 0 360px; /* Do not grow, do not shrink, base width 360px */
            margin-right: 2rem;
            opacity: 0;
            transform: translateY(20px);
            transition: opacity 0.6s ease-out, transform 0.6s ease-out;
        }

        .testimonials-section.in-view .testimonial-card {
            opacity: 1;
            transform: translateY(0);
        }

        /* Staggered animation delay for cards */
        .testimonial-card:nth-child(1) { transition-delay: 0.1s; }
        .testimonial-card:nth-child(2) { transition-delay: 0.2s; }
        .testimonial-card:nth-child(3) { transition-delay: 0.3s; }
        .testimonial-card:nth-child(4) { transition-delay: 0.4s; }
        .testimonial-card:nth-child(5) { transition-delay: 0.5s; }
        .testimonial-card:nth-child(6) { transition-delay: 0.6s; }

        .testimonial-quote {
            font-family: 'EB Garamond', serif;
            font-size: 1.2rem;
            font-style: italic;
            color: #6b5b4d;
            line-height: 1.6;
            margin-bottom: 1.5rem;
        }

        .testimonial-author {
            font-family: 'Inter', sans-serif;
            font-weight: 600;
            color: #333;
            font-size: 0.9rem;
        }

        .testimonial-author span {
            display: block;
            font-weight: 400;
            color: #8b7355;
            font-size: 0.8rem;
            margin-top: 0.25rem;
            letter-spacing: 0.05em;
        }

        /* --- CLIENT LOGO RIBBON --- */
        .client-logo-ribbon {
          background-color: #ffffff;
          padding: 3rem 0;
          border-top: 1px solid #edeae6;
          overflow: hidden;
        }

        .logo-track-wrapper {
          overflow: hidden;
          -webkit-mask-image: linear-gradient(to right, transparent 0%, black 10%, black 90%, transparent 100%);
          mask-image: linear-gradient(to right, transparent 0%, black 10%, black 90%, transparent 100%);
        }

        .logo-track {
            display: flex;
            width: fit-content;
            animation: scroll-left 40s linear infinite;
        }

        .client-logo {
            height: 40px;
            margin: 0 3rem;
            opacity: 0.6;
            transition: opacity 0.3s ease;
            filter: grayscale(100%);
        }

        .client-logo:hover {
            opacity: 1;
            filter: grayscale(0%);
        }

        /* --- SECTION 4: GALLERY CAROUSEL --- */
        .gallery-carousel-section {
          background-color: #ffffff;
          padding: 6rem 0;
          text-align: center;
          overflow: visible; /* Changed to allow hover effect */
        }

        .gallery-carousel-header {
          max-width: 800px;
          margin: 0 auto 3rem auto;
          padding: 0 2rem;
        }

        .gallery-carousel-header h2 {
          font-family: 'Inter', sans-serif;
          font-weight: 400;
          font-size: 1.3rem;
          letter-spacing: 0.3em;
          color: #8b7355;
          text-transform: uppercase;
          margin-bottom: 1.5rem;
        }

        .gallery-carousel-header p {
          font-family: 'EB Garamond', serif;
          font-size: 1.3rem;
          font-weight: 400;
          line-height: 1.6;
          color: #6b5b4d;
          margin-bottom: 0;
        }

        .gallery-carousel-cta {
          margin-top: 3rem;
        }

        .discover-button-dark {
          display: inline-block;
          padding: 14px 40px;
          border: 1.5px solid #8b7355;
          color: #8b7355;
          background-color: transparent;
          font-family: 'Inter', sans-serif;
          font-size: 0.75rem;
          font-weight: 500;
          letter-spacing: 0.15em;
          text-decoration: none;
          text-transform: uppercase;
          transition: all 0.3s ease;
        }

        .discover-button-dark:hover {
          background-color: #8b7355;
          color: #ffffff;
          transform: translateY(-1px);
        }

        /* --- IMAGE CAROUSEL STYLES --- */
        @keyframes scroll-left {
          0% { transform: translateX(0); }
          100% { transform: translateX(-50%); }
        }

        .carousel-container {
          width: 100%;
          overflow: hidden;
          padding: 2rem 0;
          position: relative;
          -webkit-mask-image: linear-gradient(to right, transparent 0%, black 5%, black 95%, transparent 100%);
          mask-image: linear-gradient(to right, transparent 0%, black 5%, black 95%, transparent 100%);
        }

        .carousel-track {
          display: flex;
          width: fit-content;
          animation: scroll-left 50s linear infinite;
        }

        .carousel-track.bottom-track {
          animation-direction: reverse;
          margin-top: 1rem;
        }

        .carousel-track.paused {
          animation-play-state: paused;
        }

        .carousel-card {
          flex-shrink: 0;
          width: 350px;
          height: 250px;
          margin: 0 0.5rem;
          border-radius: 12px;
          overflow: hidden;
          box-shadow: 0 8px 16px rgba(0, 0, 0, 0.1);
          transition: transform 0.4s cubic-bezier(0.165, 0.84, 0.44, 1), box-shadow 0.4s cubic-bezier(0.165, 0.84, 0.44, 1);
          cursor: pointer;
          border: 1px solid rgba(0, 0, 0, 0.05);
        }

        .carousel-card:hover {
          transform: scale(1.05) translateY(-5px);
          box-shadow: 0 12px 24px rgba(0, 0, 0, 0.15);
        }

        .carousel-card img {
          width: 100%;
          height: 100%;
          object-fit: cover;
        }

        /* Global class to apply blur to the main app container */
        #root.body-blur > * {
          filter: blur(10px) brightness(0.6);
          transition: filter 0.4s ease-out;
          pointer-events: none; 
        }

        /* The modal must not be blurred */
        #root.body-blur .carousel-modal {
            filter: none;
            pointer-events: auto;
        }

        /* Full-screen Modal/Lightbox */
        .carousel-modal {
          position: fixed;
          top: 0;
          left: 0;
          width: 100vw;
          height: 100vh;
          display: flex;
          align-items: center;
          justify-content: center;
          z-index: 2000;
          background-color: transparent;
          padding: 2rem;
          box-sizing: border-box;
        }

        .modal-close-button {
          position: absolute;
          top: 2rem;
          right: 2rem;
          background: rgba(0, 0, 0, 0.3);
          border: none;
          border-radius: 50%;
          width: 48px;
          height: 48px;
          cursor: pointer;
          display: flex;
          align-items: center;
          justify-content: center;
          color: white;
          transition: background-color 0.3s ease, transform 0.3s ease;
          z-index: 2001;
        }

        .modal-close-button:hover {
          background: rgba(0, 0, 0, 0.5);
          transform: rotate(90deg);
        }

        .modal-close-button svg {
          width: 28px;
          height: 28px;
        }

        .modal-content {
          max-width: 90vw;
          max-height: 90vh;
          animation: zoomIn 0.4s cubic-bezier(0.165, 0.84, 0.44, 1);
        }

        .modal-content img {
          width: 100%;
          height: 100%;
          max-width: 100%;
          max-height: 90vh;
          object-fit: contain;
          border-radius: 12px;
          box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.5);
        }

        @keyframes zoomIn {
          from { transform: scale(0.8); opacity: 0; }
          to { transform: scale(1); opacity: 1; }
        }

        /* --- RESPONSIVE STYLES --- */
        @media (max-width: 900px) {
          .services-section { flex-direction: column-reverse; }
          .services-text-column, .services-image-column { width: 100%; min-height: 400px; }
          .services-text-column { padding: 3rem 2rem; }
          .hero-text-content { margin-bottom: 3rem; }
          .hero-scroll-indicator { bottom: 2rem; }
          .testimonial-card { flex: 0 0 320px; }
        }

        @media (max-width: 600px) {
          .services-content h2 { font-size: 1rem; letter-spacing: 0.3em; }
          .services-content p { font-size: 1rem; }
          .discover-button, .discover-button-dark { padding: 12px 30px; font-size: 0.7rem; }
          .carousel-card { width: 300px; height: 215px; }
          .modal-close-button { top: 1rem; right: 1rem; width: 40px; height: 40px; }
          .modal-close-button svg { width: 24px; height: 24px; }
          .testimonials-section { padding: 6rem 0; }
          .testimonials-grid { padding-left: 1.5rem; padding-right: 1.5rem; }
          .testimonial-card { flex: 0 0 280px; padding: 2rem; margin-right: 1.5rem; }
        }
      `}</style>

      <div className="home-page-clone">
        {/* --- HERO SECTION --- */}
        <section className="hero-section">
          <div className="parallax-background" style={{ ...parallaxStyle, backgroundImage: `url(${heroImage})` }}></div>
          <div className="hero-pattern-overlay"></div>
          <div className="hero-content">
            <div className="hero-text-content">
              <h1 className="hero-main-title">Timeless</h1>
              <h2 className="hero-subtitle">Events</h2>
              <h3 className="hero-accent">Crafted with Passion</h3>
            </div>
          </div>
          <a href="#services" className="hero-scroll-indicator" aria-label="Scroll down">
            <div className="scroll-dot"></div>
          </a>
        </section>

        {/* --- SERVICES SECTION --- */}
        <section id="services" className="services-section">
          <div className="services-text-column">
            <div className="services-content">
              <h2>Our Services</h2>
              <div className="services-icon">
                <svg className="floral-ornament" viewBox="0 0 120 40" xmlns="http://www.w3.org/2000/svg">
                  <path d="M10 20 Q30 5 50 20 Q70 35 90 20 Q110 5 120 20" stroke="#8b7355" strokeWidth="2" fill="none"/>
                  <circle cx="30" cy="15" r="3" fill="#8b7355"/>
                  <circle cx="60" cy="20" r="4" fill="#8b7355"/>
                  <circle cx="90" cy="15" r="3" fill="#8b7355"/>
                </svg>
              </div>
              <p>From intimate gatherings to grand celebrations, we specialize in creating unforgettable experiences tailored to your unique vision.</p>
              <a href="/services" className="discover-button">Discover More</a>
            </div>
          </div>
          <div className="services-image-column" style={{ backgroundImage: `url(${servicesImage})` }}></div>
        </section>

        {/* --- TESTIMONIALS SECTION --- */}
        <section className="testimonials-section">
            <div className="testimonials-header">
                <h2>What Our Clients Say</h2>
                <div className="quote-icon">“</div>
            </div>
            <div className="testimonials-scroller">
                <div className="testimonials-grid">
                    {testimonials.map((testimonial, index) => (
                        <div className="testimonial-card" key={index}>
                            <p className="testimonial-quote">{testimonial.quote}</p>
                            <div className="testimonial-author">
                                {testimonial.author}
                                <span>{testimonial.event}</span>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </section>

        {/* --- CLIENT LOGO RIBBON --- */}
        <section className="client-logo-ribbon">
          <div className="logo-track-wrapper">
            <div className="logo-track">
                {[...clientLogos, ...clientLogos].map((logo, index) => (
                    <img key={index} src={logo} alt={`Client logo ${index + 1}`} className="client-logo" />
                ))}
            </div>
          </div>
        </section>

        {/* --- GALLERY CAROUSEL SECTION --- */}
        <section className="gallery-carousel-section">
          <div className="gallery-carousel-header">
            <h2>Gallery of Success</h2>
            <p>Explore a curated collection of our most memorable events, each telling a unique story of collaboration and celebration.</p>
          </div>

          <ImageCarousel images={galleryImages} />

          <div className="gallery-carousel-cta">
            <a href="/gallery" className="discover-button-dark">View Full Gallery</a>
          </div>
        </section>
      </div>
    </>
  );
};

export default Home;
