import React, { useState } from 'react';
import GalleryModals from './GalleryModals';
import './Gallery.css';

// --- NEW DATA STRUCTURE ---
// An array of objects. Each object represents a dashboard card
// and contains the images for its modal.
const galleryData = [
  {
    id: 'attestations',
    title: 'Participant Attestations',
    number: 503,
    color: '#4A90E2', // Blue
    images: [
      { id: 1, title: 'Historic Cityscapes', imageUrl: 'https://images.unsplash.com/photo-1502602898657-3e91760c0341?w=500&q=80' },
      { id: 2, title: 'Parisian Nights', imageUrl: 'https://images.unsplash.com/photo-1522093007474-d86e9bf7ba6f?w=500&q=80' },
      { id: 3, title: 'Ancient Rome', imageUrl: 'https://images.unsplash.com/photo-1525874684015-58379d421a52?w=500&q=80' },
      { id: 4, title: 'Venetian Canals', imageUrl: 'https://images.unsplash.com/photo-1518628325950-d53219853925?w=500&q=80' },
    ]
  },
  {
    id: 'sessions',
    title: 'Sessions Completed',
    number: 182,
    color: '#2E8B57', // Green
    images: [
      { id: 5, title: 'Mountain Retreats', imageUrl: 'https://images.unsplash.com/photo-1464822759023-fed622ff2c3b?w=500&q=80' },
      { id: 6, title: 'Alpine Majesty', imageUrl: 'https://images.unsplash.com/photo-1506905925346-21bda4d32df4?w=500&q=80' },
      { id: 7, title: 'Snowy Peaks', imageUrl: 'https://images.unsplash.com/photo-1549880338-65ddcdfd017b?w=500&q=80' },
      { id: 8, title: 'Misty Mornings', imageUrl: 'https://images.unsplash.com/photo-1485470733090-0aae1788d5af?w=500&q=80' },
    ]
  },
  {
    id: 'views',
    title: 'Content Views',
    number: 34,
    color: '#4A90E2', // Blue
    images: [
      { id: 10, title: 'Oceanic Wonders', imageUrl: 'https://images.unsplash.com/photo-1509316975850-ff9c5deb0cd9?w=500&q=80' },
      { id: 11, title: 'Coral Reefs', imageUrl: 'https://images.unsplash.com/photo-1551296336-55d81b459427?w=500&q=80' },
      { id: 12, title: 'Coastal Views', imageUrl: 'https://images.unsplash.com/photo-1439405326854-014607f694d7?w=500&q=80' },
    ]
  },
  {
    id: 'trainers',
    title: 'Unique Trainers',
    number: 4,
    color: '#6F4E8C', // Purple
    images: [
      { id: 13, title: 'Forest Adventures', imageUrl: 'https://images.unsplash.com/photo-1448375240586-882707db888b?w=500&q=80' },
      { id: 14, title: 'Redwood Giants', imageUrl: 'https://images.unsplash.com/photo-1542848135-ce9674066903?w=500&q=80' },
      { id: 15, title: 'Sunlit Woods', imageUrl: 'https://images.unsplash.com/photo-1425913397330-cf8af2ff40a1?w=500&q=80' },
    ]
  },
  {
    id: 'participants',
    title: 'Unique Participants',
    number: 13,
    color: '#36454F', // Dark Slate Gray
    images: [
      { id: 17, title: 'Desert Landscapes', imageUrl: 'https://images.unsplash.com/photo-1473580044384-7ba9967e16a0?w=500&q=80' },
      { id: 18, title: 'Canyon Vistas', imageUrl: 'https://images.unsplash.com/photo-1443632864897-14973fa006cf?w=500&q=80' },
      { id: 19, title: 'Dune Patterns', imageUrl: 'https://images.unsplash.com/photo-1487621814324-9a37f5492d34?w=500&q=80' },
    ]
  },
  {
    id: 'locations',
    title: 'New Locations',
    number: 24,
    color: '#2E8B57', // Green
    images: [
      { id: 20, title: 'Urban Exploration', imageUrl: 'https://images.unsplash.com/photo-1480714378408-67cf0d136b79?w=500&q=80' },
      { id: 21, title: 'Neon City', imageUrl: 'https://images.unsplash.com/photo-1534430480872-3498386e7856?w=500&q=80' },
      { id: 22, title: 'Skyscrapers', imageUrl: 'https://images.unsplash.com/photo-1523978591478-6ff514888201?w=500&q=80' },
    ]
  }
];

function Gallery() {
  const [modalState, setModalState] = useState({ isOpen: false, images: [], initialIndex: 0 });

  const handleCardClick = (cardData, imageIndex) => {
    setModalState({
      isOpen: true,
      images: cardData.images, // Pass the specific images for this card
      initialIndex: imageIndex // Start at the clicked image (or 0)
    });
  };

  const closeModal = () => {
    setModalState({ isOpen: false, images: [], initialIndex: 0 });
  };

  return (
    <>
      <div className="gallery-dashboard">
        {galleryData.map((card) => (
          <div
            key={card.id}
            className="dashboard-card"
            style={{ backgroundColor: card.color }}
            onClick={() => handleCardClick(card, 0)}
          >
            <div className="card-number">{card.number}</div>
            <div className="card-title">{card.title}</div>
          </div>
        ))}
      </div>

      <GalleryModals
        isOpen={modalState.isOpen}
        onClose={closeModal}
        images={modalState.images}
        initialIndex={modalState.initialIndex}
      />
    </>
  );
}

export default Gallery;
