import React, { useEffect, useState, useRef, useCallback } from 'react';
import './Home.css'; // Import the new CSS file

// ImageCarousel Component (remains unchanged)
const ImageCarousel = ({ images }) => {
  const [isPaused, setIsPaused] = useState(false);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedImageIndex, setSelectedImageIndex] = useState(null);

  const handleImageClick = (index) => {
    setSelectedImageIndex(index);
    setIsModalOpen(true);
  };

  const handleCloseModal = useCallback(() => {
    setIsModalOpen(false);
    setSelectedImageIndex(null);
  }, []);

  const handleNextImage = useCallback((e) => {
    e.stopPropagation();
    setSelectedImageIndex(prevIndex => (prevIndex + 1) % images.length);
  }, [images.length]);

  const handlePrevImage = useCallback((e) => {
    e.stopPropagation();
    setSelectedImageIndex(prevIndex => (prevIndex - 1 + images.length) % images.length);
  }, [images.length]);

  useEffect(() => {
    const appRoot = document.getElementById('root');
    if (appRoot) {
      if (isModalOpen) {
        appRoot.classList.add('body-blur');
      } else {
        appRoot.classList.remove('body-blur');
      }
    }

    const handleKeyDown = (e) => {
      if (e.key === 'Escape') handleCloseModal();
      else if (e.key === 'ArrowRight') handleNextImage(e);
      else if (e.key === 'ArrowLeft') handlePrevImage(e);
    };

    if (isModalOpen) {
      window.addEventListener('keydown', handleKeyDown);
    }

    return () => {
      if (appRoot) appRoot.classList.remove('body-blur');
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, [isModalOpen, handleCloseModal, handleNextImage, handlePrevImage]);

  const imageList = [...images, ...images];

  return (
    <div
      className="carousel-container"
      onMouseEnter={() => setIsPaused(true)}
      onMouseLeave={() => setIsPaused(false)}
    >
      <div className={`carousel-track top-track ${isPaused ? 'paused' : ''}`}>
        {imageList.map((img, index) => (
          <div className="carousel-card" key={`top-${index}`} onClick={() => handleImageClick(index % images.length)}>
            <img src={img} alt={`Gallery of successful team events ${index + 1}`} />
          </div>
        ))}
      </div>
      <div className={`carousel-track bottom-track ${isPaused ? 'paused' : ''}`}>
        {imageList.map((img, index) => (
          <div className="carousel-card" key={`bottom-${index}`} onClick={() => handleImageClick(index % images.length)}>
            <img src={img} alt={`Gallery of corporate culture workshops ${index + 1}`} />
          </div>
        ))}
      </div>
      {isModalOpen && selectedImageIndex !== null && (
        <div className="carousel-modal" onClick={handleCloseModal}>
          <button className="modal-close-button" onClick={handleCloseModal} aria-label="Close image view">
            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="2" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" d="M6 18L18 6M6 6l12 12" />
            </svg>
          </button>
          <button className="modal-nav-button modal-prev" onClick={handlePrevImage} aria-label="Previous image">
             <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="2" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" d="M15 19l-7-7 7-7" />
             </svg>
          </button>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <img src={images[selectedImageIndex]} alt="Full screen view of event" />
          </div>
          <button className="modal-nav-button modal-next" onClick={handleNextImage} aria-label="Next image">
             <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="2" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" d="M9 5l7 7-7 7" />
             </svg>
          </button>
        </div>
      )}
    </div>
  );
};


