import React, { useState, useEffect } from 'react';

const ServiceCard = ({ service, onOpenModal, isModalOpen, isActiveCard }) => {
  const [isAnimated, setIsAnimated] = useState(false); // Controls the lift/hover state

  // This hook ensures the card returns to its initial state when the modal is closed.
  useEffect(() => {
    if (!isModalOpen && isActiveCard) {
      setIsAnimated(false);
    }
  }, [isModalOpen, isActiveCard]);

  // --- REBUILT INTERACTION LOGIC FOR DESKTOP & MOBILE ---

  // For Desktop: Classic hover-in, hover-out.
  const handleMouseEnter = () => setIsAnimated(true);
  const handleMouseLeave = () => setIsAnimated(false);

  // For Mobile (and Desktop Clicks): This toggles the animated state.
  // On mobile, this is the primary way to lift the card.
  // On desktop, it allows a user to "lock" the card in the lifted state.
  const handleCardClick = (e) => {
    // We only toggle if the click is on the card itself, not the button inside
    if (e.target.id !== 'view_details') {
        setIsAnimated(prevState => !prevState);
    }
  };

  // The "View Details" button click handler is now the ONLY way to open the modal.
  const handleViewDetailsClick = (e) => {
    e.stopPropagation(); // Prevents the card's onClick from firing. CRITICAL FOR THE FIX.
    onOpenModal(service);
  };

  return (
    <div
      id="make-3D-space"
      onClick={handleCardClick}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      <div
        id="product-card"
        className={isAnimated ? 'animate' : ''}
      >
        <div id="product-front">
          <img src={service.image} alt={service.title} />
          <div className="image_overlay"></div>

          <div className="card-title-container">
            <h2>{service.title}</h2>
          </div>

          {/* This button's click handler is now the modal trigger */}
          <div id="view_details" onClick={handleViewDetailsClick}>
            View Details
          </div>

          <div className="stats">
            <div className="stats-container">
              <span className="product_name">{service.summaryTitle}</span>
              <p>{service.summaryDescription}</p>
              <div className="product-options">
                {service.summaryOptions.map((opt, index) => (
                  <div key={index}>
                    <strong>{opt.type}</strong>
                    <span>{opt.details}</span>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ServiceCard;
