import React, { useState } from 'react';
import Card from './Card';
import Modal from './Modal';
import './Gallery.css';

// --- DATA: Now with 6 rows of content ---
const galleryData = [
  // Row 1
  [
    {
      id: 1,
      title: 'Historic Cityscapes',
      imageUrl: 'https://images.unsplash.com/photo-1502602898657-3e91760c0341?w=500&q=80',
      fullDescription: 'Explore the rich history and stunning architecture of the world\'s most iconic cities. From the cobblestone streets of ancient capitals to sprawling modern metropolises, each view tells a story of culture, innovation, and time.',
      keyOfferings: [{ type: 'Era', details: 'Renaissance to Modern' }, { type: 'Continent', details: 'Europe' }],
    },
    {
      id: 2,
      title: 'Parisian Nights',
      imageUrl: 'https://images.unsplash.com/photo-1522093007474-d86e9bf7ba6f?w=500&q=80',
      fullDescription: 'Experience the magic of Paris after dark. The City of Light comes alive with illuminated landmarks, bustling cafes, and an unforgettable romantic atmosphere.',
      keyOfferings: [{ type: 'Landmark', details: 'Eiffel Tower' }, { type: 'Activity', details: 'Seine River Cruise' }],
    },
    { id: 3, title: 'Ancient Rome', imageUrl: 'https://images.unsplash.com/photo-1525874684015-58379d421a52?w=500&q=80', fullDescription: 'Walk in the footsteps of emperors and gladiators as you explore the timeless ruins of the Roman Forum, the Colosseum, and the Pantheon.', keyOfferings: [{type: 'Must See', details:'The Colosseum'}, {type: 'Era', details:'753 BC - 476 AD'}] },
    { id: 4, title: 'Venetian Canals', imageUrl: 'https://images.unsplash.com/photo-1518628325950-d53219853925?w=500&q=80', fullDescription: 'Discover the unique charm of Venice, a city built on water. Glide through its winding canals on a gondola and marvel at the stunning architecture that lines the waterways.', keyOfferings: [{type: 'Transport', details: 'Gondola, Vaporetto'}, {type: 'Highlight', details: 'Rialto Bridge'}] },
  ],
  // Row 2
  [
    { id: 5, title: 'Mountain Retreats', imageUrl: 'https://images.unsplash.com/photo-1464822759023-fed622ff2c3b?w=500&q=80', fullDescription: 'Escape to the serene beauty of the mountains. Breathe in the crisp, fresh air and find peace among towering peaks and lush valleys.', keyOfferings: [] },
    { id: 6, title: 'Alpine Majesty', imageUrl: 'https://images.unsplash.com/photo-1506905925346-21bda4d32df4?w=500&q=80', fullDescription: 'Description for Alpine Majesty...', keyOfferings: [] },
    { id: 7, title: 'Snowy Peaks', imageUrl: 'https://images.unsplash.com/photo-1549880338-65ddcdfd017b?w=500&q=80', fullDescription: 'Description for Snowy Peaks...', keyOfferings: [] },
  ],
  // Row 3
  [
    { id: 10, title: 'Oceanic Wonders', imageUrl: 'https://images.unsplash.com/photo-1509316975850-ff9c5deb0cd9?w=500&q=80', fullDescription: 'Description for Oceanic Wonders...', keyOfferings: [] },
    { id: 11, title: 'Coral Reefs', imageUrl: 'https://images.unsplash.com/photo-1551296336-55d81b459427?w=500&q=80', fullDescription: 'Description for Coral Reefs...', keyOfferings: [] },
    { id: 12, title: 'Coastal Views', imageUrl: 'https://images.unsplash.com/photo-1439405326854-014607f694d7?w=500&q=80', fullDescription: 'Description for Coastal Views...', keyOfferings: [] },
  ],
  // Row 4 - NEW CONTENT
  [
    { id: 13, title: 'Forest Adventures', imageUrl: 'https://images.unsplash.com/photo-1448375240586-882707db888b?w=500&q=80', fullDescription: 'Lose yourself in the enchanting depths of the forest. Discover hidden paths, ancient trees, and the vibrant life within the woods.', keyOfferings: [{ type: 'Activity', details: 'Hiking, Camping' }] },
    { id: 14, title: 'Sunlit Woods', imageUrl: 'https://images.unsplash.com/photo-1425913397330-cf8af2ff40a1?w=500&q=80', fullDescription: 'Witness the breathtaking beauty of sunlight filtering through the canopy, creating a magical, dappled light on the forest floor.', keyOfferings: [{ type: 'Best Time', details: 'Golden Hour' }] },
  ],
  // Row 5 - NEW CONTENT
  [
    { id: 15, title: 'Desert Landscapes', imageUrl: 'https://images.unsplash.com/photo-1473580044384-7ba9967e16a0?w=500&q=80', fullDescription: 'Explore the vast, arid beauty of the desert. A landscape of stark contrasts, rolling dunes, and resilient life.', keyOfferings: [{ type: 'Climate', details: 'Arid' }] },
    { id: 16, title: 'Canyon Vistas', imageUrl: 'https://images.unsplash.com/photo-1443632864897-14973fa006cf?w=500&q=80', fullDescription: 'Gaze into the immense, layered history of the earth from the rim of a majestic canyon.', keyOfferings: [{ type: 'Feature', details: 'Rock Formations' }] },
    { id: 17, title: 'Dune Patterns', imageUrl: 'https://images.unsplash.com/photo-1487621814324-9a37f5492d34?w=500&q=80', fullDescription: 'The wind sculpts the sand into ever-changing, mesmerizing patterns, creating a landscape that is never the same twice.', keyOfferings: [{ type: 'Element', details: 'Wind & Sand' }] },
  ],
  // Row 6 - NEW CONTENT
  [
    { id: 18, title: 'Urban Exploration', imageUrl: 'https://images.unsplash.com/photo-1480714378408-67cf0d136b79?w=500&q=80', fullDescription: 'Dive into the vibrant energy of city life. Explore towering skyscrapers, bustling streets, and the endless stories of a metropolis.', keyOfferings: [] },
    { id: 19, title: 'Neon City', imageUrl: 'https://images.unsplash.com/photo-1534430480872-3498386e7856?w=500&q=80', fullDescription: 'Experience the city\'s transformation as night falls and neon lights paint the streets in a dazzling array of colors.', keyOfferings: [] },
  ],
];

const Gallery = () => {
  // State is now much simpler: we only need to track the modal's state.
  const [modalState, setModalState] = useState({ isOpen: false, rowIndex: null, cardIndex: null });

  // This handler is now simplified. It directly sets the modal state.
  // No need to check for an "expanded" row. THIS FIXES THE BUG.
  const handleCardClick = (rowIndex, cardIndex) => {
    setModalState({ isOpen: true, rowIndex, cardIndex });
  };

  const closeModal = () => {
    setModalState({ isOpen: false, rowIndex: null, cardIndex: null });
  };

  return (
    <>
      <div className="gallery-wrapper">
        {galleryData.map((row, rowIndex) => (
          // Each row is now just a div containing the card grid.
          <div key={rowIndex} className="card-row">
            <ul className="card-grid">
              {row.map((card, cardIndex) => (
                <Card
                  key={card.id}
                  card={card}
                  // Pass a simplified click handler to the Card component.
                  onCardClick={() => handleCardClick(rowIndex, cardIndex)}
                />
              ))}
            </ul>
          </div>
        ))}
      </div>

      <Modal
        isOpen={modalState.isOpen}
        onClose={closeModal}
        galleryData={galleryData}
        initialState={modalState}
      />
    </>
  );
};

export default Gallery;
