          border: none;
          color: white;
          font-size: 2.5rem;
          cursor: pointer;
          padding: 20px 15px;
          border-radius: 5px;
          opacity: 0.7;
          transition: all 0.2s ease;
          user-select: none;
          z-index: 1010;
        }

        .modal-nav-btn:hover {
          background: rgba(0, 0, 0, 0.6);
          opacity: 1;
        }

        .modal-nav-btn.prev {
          left: 20px;
        }

        .modal-nav-btn.next {
          right: 20px;
        }

        /* Responsive Design */
        @media (max-width: 768px) {
          .gallery-grid {
            grid-template-columns: 1fr;
            gap: 30px;
          }

          .card-row.is-expanded .card-stack {
            gap: 15px;
          }

          .card {
            width: 220px;
          }

          .modal-content {
            margin: 10px;
          }
        }
      `}</style>

      <div className="gallery-wrapper">
        <div className="gallery-grid">
          {galleryData.map((row, rowIndex) => (
            <div
              key={rowIndex}
              className={`card-row ${expandedRowIndex === rowIndex ? 'is-expanded' : ''}`}
            >
              <ul
                className="card-stack"
                onClick={expandedRowIndex !== rowIndex ? () => handleRowClick(rowIndex) : undefined}
              >
                {row.map((card, cardIndex) => (
                  <Card
                    key={card.id}
                    card={card}
                    index={cardIndex}
                    isExpanded={expandedRowIndex === rowIndex}
                    onCardClick={(e) => handleCardClick(e, rowIndex, cardIndex)}
                    onCollapse={handleCollapseRow}
                    isFirst={cardIndex === 0}
                  />
                ))}
              </ul>
            </div>
          ))}
        </div>
      </div>

      <Modal
        isOpen={modalState.isOpen}
        onClose={closeModal}
        galleryData={galleryData}
        initialState={modalState}
      />
    </>
  );
};

export default Gallery;
