import React, { useState } from 'react';
import GalleryModals from './GalleryModals';
import './Gallery.css';

// --- DATA STRUCTURE with detailed descriptions for the modal ---
const galleryData = [
  // Row 1
  [
    { 
      id: 1, 
      title: 'Historic Cityscapes', 
      imageUrl: 'https://images.unsplash.com/photo-1502602898657-3e91760c0341?w=500&q=80',
      fullDescription: "Journey through time as you explore the intricate architecture and cobblestone streets of the world's most historic city centers. Each corner tells a story of a bygone era.",
      keyOfferings: [
        { type: "Guided Tours", details: "Daily walking tours with expert local historians." },
        { type: "Photography Ops", details: "Designated photo spots for capturing the perfect shot." }
      ]
    },
    { id: 2, title: 'Parisian Nights', imageUrl: 'https://images.unsplash.com/photo-1522093007474-d86e9bf7ba6f?w=500&q=80' },
    { id: 3, title: 'Ancient Rome', imageUrl: 'https://images.unsplash.com/photo-1525874684015-58379d421a52?w=500&q=80' },
    { id: 4, title: 'Venetian Canals', imageUrl: 'https://images.unsplash.com/photo-1518628325950-d53219853925?w=500&q=80' },
  ],
  // Row 2
  [
    { 
      id: 5, 
      title: 'Mountain Retreats', 
      imageUrl: 'https://images.unsplash.com/photo-1464822759023-fed622ff2c3b?w=500&q=80',
      fullDescription: "Escape to the tranquility of the mountains. Our retreats offer breathtaking views, crisp alpine air, and a chance to reconnect with nature's grandeur.",
      keyOfferings: [
        { type: "Accommodation", details: "Cozy cabins and luxury lodges available." },
        { type: "Activities", details: "Hiking, skiing, and meditation sessions." }
      ]
    },
    { id: 6, title: 'Alpine Majesty', imageUrl: 'https://images.unsplash.com/photo-1506905925346-21bda4d32df4?w=500&q=80' },
    { id: 7, title: 'Snowy Peaks', imageUrl: 'https://images.unsplash.com/photo-1549880338-65ddcdfd017b?w=500&q=80' },
    { id: 8, title: 'Misty Mornings', imageUrl: 'https://images.unsplash.com/photo-1485470733090-0aae1788d5af?w=500&q=80' },
    { id: 9, title: 'Hiking Trails', imageUrl: 'https://images.unsplash.com/photo-1458442310124-05424337147c?w=500&q=80' },
  ],
  // Other rows...
];

const BackArrowIcon = ({ onClick }) => (
  <div className="back-arrow" onClick={onClick} title="Collapse Row">
    <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor">
      <path d="M10.8284 12.0007L15.7782 16.9504L14.364 18.3646L8 12.0007L14.364 5.63672L15.7782 7.05093L10.8284 12.0007Z"></path>
    </svg>
  </div>
);


function Gallery() {
  const [expandedRowIndex, setExpandedRowIndex] = useState(null);
  const [modalState, setModalState] = useState({ isOpen: false, rowIndex: null, cardIndex: null });

  const handleRowClick = (rowIndex) => {
    // FIX: This now only handles EXPANDING a row.
    // If a row is already open, clicking the container does nothing.
    if (expandedRowIndex !== rowIndex) {
      setExpandedRowIndex(rowIndex);
    }
  };

  const handleCollapseRow = (e) => {
    e.stopPropagation(); 
    setExpandedRowIndex(null);
  };

  const handleCardClick = (e, rowIndex, cardIndex) => {
    // Only open modal if the row is expanded
    if (expandedRowIndex === rowIndex) {
      e.stopPropagation(); // This is crucial to prevent the row from collapsing
      setModalState({ isOpen: true, rowIndex, cardIndex });
    } else {
        // If the row is not expanded, treat the click as a row click to open it
        handleRowClick(rowIndex);
    }
  };

  const closeModal = () => {
    setModalState({ isOpen: false, rowIndex: null, cardIndex: null });
  };

  return (
    <>
      <div className="gallery-wrapper">
        {galleryData.map((row, rowIndex) => (
          <div
            key={rowIndex}
            className={`card-row ${expandedRowIndex === rowIndex ? 'is-expanded' : ''}`}
          >
            <ul
              className="card-stack"
              // FIX: The click handler is now on the card, not the container, to avoid conflicts
            >
              {row.map((card, cardIndex) => (
                <li
                  key={card.id}
                  className="card"
                  style={{ '--index': cardIndex }}
                  onClick={(e) => handleCardClick(e, rowIndex, cardIndex)}
                >
                  {expandedRowIndex === rowIndex && cardIndex === 0 && (
                    <BackArrowIcon onClick={handleCollapseRow} />
                  )}
                  <img src={card.imageUrl} alt={card.title} />
                </li>
              ))}
            </ul>
          </div>
        ))}
      </div>

      <GalleryModals
        isOpen={modalState.isOpen}
        onClose={closeModal}
        galleryData={galleryData}
        initialState={modalState}
      />
    </>
  );
}

export default Gallery;
